CREATE OR REPLACE PROCEDURE gerar_mapa_setores (dt_dieta_p timestamp, cd_setor_atendimento_p bigint, qt_horas_p bigint, cd_refeicao_p text, cd_estabelecimento_p bigint default null, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE

			 
 
ie_bloqueia_mapa_refeicao_w	varchar(1);
ds_horario_w			varchar(19);
qt_regra_mapa_w			bigint;
			

BEGIN 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	select	consistir_regra_mapa_refeicao(dt_dieta_p,cd_refeicao_p)  
	into STRICT 	qt_regra_mapa_w 
	;
		 
	if (qt_regra_mapa_w > 0) then 
		begin 
		ie_bloqueia_mapa_refeicao_w := 'S';
		end;
	else 
		begin 
		ie_bloqueia_mapa_refeicao_w := 'N';
		end;
	end if;
		 
		if (ie_bloqueia_mapa_refeicao_w = 'N') then 
			begin 
			CALL gerar_mapa_dieta(cd_setor_atendimento_p, 
					nm_usuario_p, 
					dt_dieta_p, 
					cd_refeicao_p, 
					qt_horas_p, 
					cd_estabelecimento_p, 
					'N');
			end;
		else 
			begin 
			select	ds_horario 
			into STRICT	ds_horario_w 
			from	mapa_regra_horario 
			where	cd_refeicao = cd_refeicao_p;
				 
			--Exite restrição de horário ( permitido lançamento até às #@DATA#@ ) para a refeição ou data do mapa menor que a data atual. 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(60433,'DATA='||ds_horario_w);			
			end;
		end if;
	end;
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
