CREATE OR REPLACE PROCEDURE gerar_material_devolucao ( cd_material_p bigint, ie_limpar_p text, nm_usuario_p text) AS $body$
DECLARE


ds_material_w	varchar(255);


BEGIN

if (ie_limpar_p = 'S') then
	delete from material_devolucao_w where nm_usuario = nm_usuario_p;
else
	select	max(ds_material)
	into STRICT	ds_material_w
	from	material
	where	cd_material = cd_material_p;

	insert into material_devolucao_w(nr_sequencia,
		cd_material,
		ds_material,
		dt_atualizacao,
		nm_usuario)
	values (nextval('material_devolucao_w_seq'),
		cd_material_p,
		ds_material_w,
		clock_timestamp(),
		nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
