SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_material_equipamento ( cd_estabelecimento_p bigint, nr_seq_equipamento_p bigint, nm_usuario_p text ) AS $body$
DECLARE


cd_equipamento_w		bigint;
nr_seq_classif_equip_w		bigint;
nr_seq_proc_w			bigint;
nr_cirurgia_w			bigint;
cd_material_w			integer;
qt_material_w			double precision;
cd_unidade_medida_w		varchar(30);
nr_seq_equi_cir_w		bigint;
ie_gera_quantidade_w		varchar(1);
ds_lista_nao_atualiza_qtd_w 	varchar(255);
qt_equipamento_w		bigint;


C01 CURSOR FOR
	SELECT	cd_material,
		qt_material,
		cd_unidade_medida
	from	material_equipamento
	where	coalesce(cd_equipamento,cd_equipamento_w)		= cd_equipamento_w
	and	coalesce(nr_seq_classif_equip, coalesce(nr_seq_classif_equip_w,0))	= coalesce(nr_seq_classif_equip_w,0)
	and	coalesce(nr_seq_proc, coalesce(nr_seq_proc_w,0))			= coalesce(nr_seq_proc_w,0)
	and	cd_estabelecimento 				= cd_estabelecimento_p;


BEGIN

ie_gera_quantidade_w := Obter_Param_Usuario(872, 296, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_gera_quantidade_w);
ds_lista_nao_atualiza_qtd_w := Obter_Param_Usuario(872, 331, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ds_lista_nao_atualiza_qtd_w);


select	nr_cirurgia,
	cd_equipamento,
	campo_numerico(obter_dados_equipamento(cd_equipamento,'C')),
	qt_equipamento
into STRICT	nr_cirurgia_w,
	cd_equipamento_w,
	nr_seq_classif_equip_w,
	qt_equipamento_w
from	equipamento_cirurgia
where	nr_sequencia	=	nr_seq_equipamento_p
and	coalesce(ie_situacao,'A') = 'A';

select	max(nr_seq_proc_interno)
into STRICT	nr_seq_proc_w
from	cirurgia
where	nr_cirurgia	=	nr_cirurgia_w;

if (ie_gera_quantidade_w = 'S') and (ds_lista_nao_atualiza_qtd_w IS NOT NULL AND ds_lista_nao_atualiza_qtd_w::text <> '') and (obter_se_contido_char(cd_equipamento_w,ds_lista_nao_atualiza_qtd_w) = 'S') then
	ie_gera_quantidade_w := 'N';
end if;

open C01;
loop
fetch C01 into
	cd_material_w,
	qt_material_w,
	cd_unidade_medida_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('material_equip_cirurgia_seq')
	into STRICT	nr_seq_equi_cir_w
	;

	insert into material_equip_cirurgia(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_material,
		qt_material,
		cd_unidade_medida,
		nr_seq_equi_cir)
	values (nr_seq_equi_cir_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_material_w,
		CASE WHEN ie_gera_quantidade_w='S' THEN qt_equipamento_w  ELSE qt_material_w END ,
		cd_unidade_medida_w,
		nr_seq_equipamento_p);
	end;
end loop;
close C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
