CREATE OR REPLACE PROCEDURE gerar_material_lote_fornec_imp ( nm_usuario_p text, nr_seq_lote_fornec_p bigint, qt_impressao_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_impressas_w		material_lote_fornec_imp.qt_impressao%type;
qt_material_w		bigint;


BEGIN

select	nextval('material_lote_fornec_imp_seq')
into STRICT	nr_sequencia_w
;

insert into material_lote_fornec_imp(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					nr_seq_lote_fornec,
					qt_impressao)
			      values (	nr_sequencia_w,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_lote_fornec_p,
					qt_impressao_p);

select	sum(qt_impressao)
into STRICT	qt_impressas_w
from	material_lote_fornec_imp
where	nr_seq_lote_fornec = nr_seq_lote_fornec_p;

select	qt_material
into STRICT	qt_material_w
from	material_lote_fornec
where	nr_sequencia = nr_seq_lote_fornec_p;

if (qt_impressas_w > qt_material_w) then
	CALL gerar_comunic_imp_lote_fornec(nm_usuario_p, cd_estabelecimento_p, nr_seq_lote_fornec_p, qt_impressao_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
