CREATE OR REPLACE PROCEDURE gerar_material_marca_oc ( nr_ordem_compra_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w		integer;
nr_seq_marca_w		bigint;
qt_existe_w		bigint;
ds_observacao_w		varchar(255);

c01 CURSOR FOR
SELECT	distinct
	cd_material,
	nr_seq_marca
from	ordem_compra_item a
where	nr_ordem_compra = nr_ordem_compra_p
and	(nr_seq_marca IS NOT NULL AND nr_seq_marca::text <> '')
and	coalesce(dt_reprovacao::text, '') = ''
and	not exists (	select	1
			from	material_marca x
			where	x.nr_sequencia = a.nr_seq_marca
			and	x.cd_material = a.cd_material);


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	ordem_compra_item a
where	nr_ordem_compra = nr_ordem_compra_p
and	(nr_seq_marca IS NOT NULL AND nr_seq_marca::text <> '')
and	coalesce(dt_reprovacao::text, '') = ''
and	not exists (SELECT	1
		from	material_marca x
		where	x.nr_sequencia = a.nr_seq_marca
		and	x.cd_material = a.cd_material);

if (qt_existe_w > 0) then
	begin

	ds_observacao_w	:=	substr(wheb_mensagem_pck.get_texto(301665, 'NR_ORDEM_COMPRA=' || nr_ordem_compra_p),1,255);

	open c01;
	loop
	fetch c01 into
		cd_material_w,
		nr_seq_marca_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		insert into material_marca(
			nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			cd_material,
			ie_situacao,
			cd_estabelecimento,
			ds_observacao) values (
				nr_seq_marca_w,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				cd_material_w,
				'A',
				cd_estabelecimento_p,
				ds_observacao_w);
		end;
	end loop;
	close c01;

	end;
end if;

/*Não pode dar commit pois é chamada dentro de trigger */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
