CREATE OR REPLACE PROCEDURE gerar_mat_alerta_execucao ( cd_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_alerta_p INOUT text, ie_alerta_barras_p text, cd_convenio_p bigint) AS $body$
DECLARE



ds_alerta_w			varchar(255):='';
cd_classe_material_w            integer;
cd_grupo_material_w             smallint;
cd_subgrupo_material_w          smallint;

C01 CURSOR FOR
	SELECT	ds_alerta
	from	mat_alerta_execucao
	where	coalesce(cd_classe_material,coalesce(cd_classe_material_w,0))	= coalesce(cd_classe_material_w,0)
	and	coalesce(cd_grupo_material,coalesce(cd_grupo_material_w,0))	= coalesce(cd_grupo_material_w,0)
	and	coalesce(cd_subgrupo_material,coalesce(cd_subgrupo_material_w,0))	= coalesce(cd_subgrupo_material_w,0)
	and	coalesce(cd_material,coalesce(cd_material_p,0))			= coalesce(cd_material_p,0)
	and	((ie_alerta_barras_p = 'N') or (ie_alerta_barras_p = 'S' and coalesce(ie_alerta_barras,'N') = 'S'))
	and	coalesce(cd_convenio,coalesce(cd_convenio_p,0))			= coalesce(cd_convenio_p,0)
	order by	coalesce(cd_convenio,0),
			coalesce(cd_grupo_material_w,0),
			coalesce(cd_subgrupo_material_w,0),
			coalesce(cd_classe_material_w,0),
			coalesce(cd_material_p,0),
			coalesce(ie_alerta_barras,'N');



BEGIN


if (coalesce(cd_material_p,0) > 0) then

	begin

	select 	cd_classe_material,
		cd_grupo_material,
		cd_subgrupo_material
	into STRICT	cd_classe_material_w,
		cd_grupo_material_w,
		cd_subgrupo_material_w
	from	estrutura_material_v
	where	cd_material =  cd_material_p;

	open C01;
	loop
	fetch C01 into
		ds_alerta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
	exception when
		others then
		ds_alerta_w:= '';
	end;

end if;

ds_alerta_p := ds_alerta_w;

commit;

end;
$body$
