CREATE OR REPLACE PROCEDURE gerar_mat_req_reg ( cd_material_p bigint, qt_material_p bigint, nr_seq_req_reg_p bigint, nm_usuario_p text) AS $body$
DECLARE

								
								
sqlerrm_w				varchar(4000);
nr_seq_material_w		bigint;


BEGIN

begin

Select  max(nr_sequencia)
into STRICT	nr_seq_material_w
from	pls_material
where 	cd_material = cd_material_p
and		coalesce(ie_situacao,'A') = 'A';

if (nr_seq_material_w > 0 ) then

	insert into Pls_requisicao_mat(	nr_sequencia,
								nr_seq_requisicao,
								dt_atualizacao,
								nm_usuario,
								ie_status,
								ie_estagio,
								nr_seq_material,
								cd_material,
								qt_material,
								qt_solicitado)
					values (		nextval('pls_requisicao_mat_seq'),
								nr_seq_req_reg_p,
								clock_timestamp(),
								nm_usuario_p,
								'M',
								'M',
								nr_seq_material_w,
								cd_material_p,
								qt_material_p,
								qt_material_p);
								
end if;								

commit;

exception
when others then
		sqlerrm_w	:= sqlerrm;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
