SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_medicamento_cod_barras ( nr_cirurgia_p bigint, cd_material_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_agente_p bigint, ie_via_aplicacao_p text, nr_seq_pepo_p bigint, ie_opcao_p text, ds_just_alergico_p text default null) AS $body$
DECLARE



cd_pessoa_fisica_w	varchar(10);
nr_seq_agente_w		bigint;
qt_agente_w		smallint;
ds_forma_lib_w		varchar(255);
ie_permite_registrar_w	varchar(1) := 'S';
nr_seq_w		bigint;
nr_seq_pepo_w		bigint;
qt_dose_w		smallint;
ie_permite_lanc_disp_w  varchar(1) := 'S';


BEGIN

ds_forma_lib_w := Obter_Param_Usuario(872, 26, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ds_forma_lib_w);

select	obter_pessoa_fisica_usuario(nm_usuario_p,'C')
into STRICT	cd_pessoa_fisica_w
;

if (nr_seq_agente_p > 0) then
	nr_seq_agente_w := nr_seq_agente_p;
else
	select	coalesce(max(a.nr_seq_agente),0)
	into STRICT	nr_seq_agente_w
	from	agente_anest_material a,
		agente_anestesico b
	where	b.nr_sequencia	=	a.nr_seq_agente
	and	a.cd_material	=	cd_material_p
	and	b.ie_situacao	=	'A';
end if;
	

if (nr_seq_agente_w = 0) then
	select	coalesce(max(a.nr_seq_agente),0)
	into STRICT	nr_seq_agente_w
	from	material b,
		agente_anest_material a,
		agente_anestesico c
	where	a.nr_seq_agente		=	c.nr_sequencia
	and	a.cd_material		=	b.cd_material
	and	b.cd_material_generico	=	cd_material_p
	and	c.ie_situacao		=	'A';
end if;

if (nr_seq_agente_w = 0) then
	select	coalesce(max(a.nr_seq_agente),0)
	into STRICT	nr_seq_agente_w
	from	material b,
		agente_anest_material a,
		agente_anestesico c
	where	a.nr_seq_agente		=	c.nr_sequencia
	and	a.cd_material		=	b.cd_material_generico
	and	b.cd_material		=	cd_material_p
	and	c.ie_situacao		=	'A';
end if;

if (ds_forma_lib_w IS NOT NULL AND ds_forma_lib_w::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_permite_registrar_w
	from	agente_anestesico
	where	obter_se_contido_char(ie_forma_farmaceutica,ds_forma_lib_w) = 'S'
	and	nr_sequencia = nr_seq_agente_w;
end if;

if (nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') then
	nr_seq_pepo_w := nr_seq_pepo_p;
else	
	nr_seq_pepo_w := null;
end if;

select	obter_permite_lanc_disp_pepo(cd_material_p, nr_seq_agente_w, ie_opcao_p)
into STRICT	ie_permite_lanc_disp_w
;


if (ie_permite_registrar_w = 'S') and (ie_permite_lanc_disp_w <> 'N') then
	begin
	select	count(*),
		coalesce(max(qt_dose),0)
	into STRICT	qt_agente_w,
		qt_dose_w
	from	cirurgia_agente_anestesico
	where	nr_cirurgia	=	nr_cirurgia_p
	and	nr_seq_agente	=	nr_seq_agente_w
	and	coalesce(ie_situacao,'A') = 'A';
			
	if (nr_seq_agente_w > 0) and (qt_agente_w = 0) then
		nr_seq_w := gerar_agentes_medicamentos(cd_estabelecimento_p, nr_seq_agente_w, nr_cirurgia_p, cd_material_p, cd_pessoa_fisica_w, nm_usuario_p, nr_seq_w, ie_via_aplicacao_p, null, null, nr_seq_pepo_w, null, null, ds_just_alergico_p);
	elsif (nr_seq_agente_w > 0) and (qt_agente_w > 0) then
			
		update	cirurgia_agente_anestesico
		set	cd_material		=	cd_material_p,
			qt_dose = qt_dose_w + 1,
         ds_just_alergico = substr(ds_just_alergico_p,1,255)
		where	coalesce(nr_cirurgia,0)	=	coalesce(nr_cirurgia_p,0)
		and	nr_seq_agente		=	nr_seq_agente_w
		and	coalesce(nr_seq_pepo,0)		= coalesce(nr_seq_pepo_p,0)
		and	coalesce(ie_situacao,'A') = 'A'
		and	coalesce(dt_liberacao::text, '') = '';

		commit;
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
