CREATE OR REPLACE PROCEDURE gerar_medic_dose_onc (nr_seq_interno_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w		bigint;
qt_dose_prescricao_w	double precision;
ds_dia_ciclo_w		varchar(10);
nr_seq_paciente_w	bigint;
nr_seq_atendimento_w	bigint;



BEGIN

select 	max(cd_material),
	max(qt_dose_prescricao)
into STRICT	cd_material_w,
	qt_dose_prescricao_w
from	paciente_atend_medic
where	nr_seq_interno = nr_seq_interno_p;

select 	max(nr_seq_atendimento)
into STRICT	nr_seq_atendimento_w
from	paciente_atend_medic
where	nr_seq_interno = nr_seq_interno_p;

select	max(ds_dia_ciclo)
into STRICT	ds_dia_ciclo_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_w;

select 	max(nr_seq_paciente)
into STRICT	nr_seq_paciente_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_w;

select 	max(a.nr_seq_atendimento)
into STRICT	nr_seq_atendimento_w
from	paciente_atendimento a
where	a.ds_dia_ciclo = ds_dia_ciclo_w
and	a.nr_seq_paciente = nr_seq_paciente_w
and	a.nr_ciclo = 	(SELECT max(nr_ciclo)
			from	paciente_atendimento e
			where	a.nr_seq_paciente = e.nr_seq_paciente);


update	paciente_atend_medic
set	qt_dose_prescricao = qt_dose_prescricao_w
where	nr_seq_atendimento = nr_seq_atendimento_w
and	cd_material = cd_material_w;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
