SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_medida_laudo_peca ( nr_seq_laudo_medida_peca_p bigint, nm_usuario_p text ) AS $body$
DECLARE



nr_seq_medida_exame_w	bigint;
nr_seq_laudo_w		bigint;
vl_padrao_w		medida_exame_laudo.vl_padrao%type;

qt_valor_minimo_w	double precision;
qt_valor_maximo_w	double precision;
ds_valor_medida_w	varchar(255);
qt_medida_w		double precision;

ie_tipo_medida_w	varchar(15);

qt_anos_w		integer;
qt_peso_prescr_w	integer;
ie_sexo_w		varchar(10);

nr_prescricao_w		bigint;
dt_laudo_w		timestamp;

qt_peso_w		double precision;
qt_altura_cm_w		double precision;
ie_tipo_valor_w		varchar(5);

nr_seq_peca_w		bigint;
nr_seq_tipo_medida_w	bigint;



c01 CURSOR FOR
	SELECT	m.nr_sequencia,
		upper(m.vl_padrao)
	from	medida_exame_laudo m,
		tipo_medida_laudo t
	where	m.nr_seq_tipo_medida 	= t.nr_sequencia
	and	t.nr_sequencia 		= nr_seq_tipo_medida_w
	and	m.ie_situacao 		= 'A'
	order	by m.nr_seq_apresentacao, m.nr_sequencia;

c02 CURSOR FOR
	SELECT	qt_valor_minimo,
		qt_valor_maximo
	from	medida_exame_refer
	where	nr_seq_medida = nr_seq_medida_exame_w
	and (ie_sexo = ie_sexo_w or ie_sexo = 'A')
	and	qt_anos_w between qt_idade_minima and qt_idade_maxima
	and	qt_peso_prescr_w between coalesce(qt_peso_minimo,qt_peso_prescr_w) and coalesce(qt_peso_maximo,qt_peso_prescr_w)
	and (coalesce(ie_tipo_medida,1) = coalesce(coalesce(ie_tipo_medida_w, ie_tipo_medida) ,1))
	order	by ie_sexo;


BEGIN

CALL delete_medida_laudo_peca(nr_seq_laudo_medida_peca_p);


select	max(nr_seq_peca),
	max(nr_seq_tipo_medida),
	max(nr_seq_laudo)
into STRICT	nr_seq_peca_w,
	nr_seq_tipo_medida_w,
	nr_seq_laudo_w
from	laudo_medida_peca
where	nr_sequencia 	= nr_seq_laudo_medida_peca_p;


select	max(ie_tipo_medida),
	max(nr_prescricao),
	max(dt_laudo)
into STRICT	ie_tipo_medida_w,
	nr_prescricao_w,
	dt_laudo_w
from	laudo_paciente
where	nr_sequencia = nr_seq_laudo_w;

select	(obter_idade(b.dt_nascimento, dt_laudo_w, 'A'))::numeric ,
	coalesce(b.ie_sexo,'A')
into STRICT	qt_anos_w,
	ie_sexo_w
from	pessoa_fisica b,
	prescr_medica a
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and	a.nr_prescricao 	= nr_prescricao_w;

select	coalesce(obter_peso_prescr_sinal(nr_prescricao_w),0)
into STRICT	qt_peso_prescr_w
;

open C01;
loop
fetch C01 into
	nr_seq_medida_exame_w,
	vl_padrao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	qt_valor_minimo_w	:= null;
	qt_valor_maximo_w	:= null;

	open c02;
	loop
	fetch c02 into
		qt_valor_minimo_w,
		qt_valor_maximo_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	end loop;
	close c02;

	ds_valor_medida_w	:= '';
	qt_medida_w		:= null;
	if (vl_padrao_w = '@PESO') then

		select	qt_peso
		into STRICT	qt_peso_w
		from	prescr_medica
		where	nr_prescricao	= nr_prescricao_w;

		if (ie_tipo_valor_w = 'N') then
			qt_medida_w		:= qt_peso_w;
		else
			ds_valor_medida_w	:= qt_peso_w;
		end if;

	elsif (vl_padrao_w = '@ALTURA') then

		select	qt_altura_cm
		into STRICT	qt_altura_cm_w
		from	prescr_medica
		where	nr_prescricao	= nr_prescricao_w;

		if (ie_tipo_valor_w = 'N') then
			qt_medida_w		:= qt_altura_cm_w;
		else
			ds_valor_medida_w	:= qt_altura_cm_w;
		end if;

	end if;


	insert 	into 	laudo_paciente_medida(
			nr_sequencia,
			nr_seq_laudo,
			nr_seq_medida,
			dt_atualizacao,
			nm_usuario,
			qt_minimo,
			qt_maximo,
			ds_valor_medida,
			qt_medida,
			nr_seq_peca_medida
			)
	values (
			nextval('laudo_paciente_medida_seq'),
			nr_seq_laudo_w,
			nr_seq_medida_exame_w,
			clock_timestamp(),
			nm_usuario_p,
			qt_valor_minimo_w,
			qt_valor_maximo_w,
			ds_valor_medida_w,
			qt_medida_w,
			nr_seq_laudo_medida_peca_p
			);

	end;
end loop;
close C01;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
