SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_med_paciente_atend ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_medico_p text, ie_commit_p text default 'S') AS $body$
DECLARE


nr_seq_cliente_w			med_cliente.nr_sequencia%Type;
nr_count_med_pac_atend_w	bigint;
ie_nivel_atencao_w			varchar(1);


BEGIN

ie_nivel_atencao_w := wheb_assist_pck.get_nivel_atencao_perfil();

if (ie_nivel_atencao_w = 'S') then

	Select  coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_cliente_w
	from	med_cliente
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and		cd_medico = cd_medico_p;

	If (nr_seq_cliente_w = 0) then
		nr_seq_cliente_w := sec_incluir_med_cliente(cd_pessoa_fisica_p, cd_medico_p, wheb_usuario_pck.get_nm_usuario, ie_commit_p, nr_seq_cliente_w);
	end if;

	if (nr_seq_cliente_w > 0) then
	-- Criar a integridade na tabela med_paciente_cli
		select	count(1)
		into STRICT	nr_count_med_pac_atend_w
		from	med_paciente_atend
		where	nr_seq_cliente = nr_seq_cliente_w
		and		nr_atendimento = nr_atendimento_p;

		if (nr_count_med_pac_atend_w = 0) then

			insert into med_paciente_atend(	nr_atendimento,
										nr_sequencia,
										dt_atualizacao,
										nm_usuario,
										dt_atualizacao_nrec,
										nr_seq_cliente
										) values (
										nr_atendimento_p,
										nextval('med_paciente_atend_seq'),
										clock_timestamp(),
										wheb_usuario_pck.get_nm_usuario,
										clock_timestamp(),
										nr_seq_cliente_w);

			if (ie_commit_p = 'S') then
				commit;
			end if;

		end if;

	end if;

end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
