CREATE OR REPLACE PROCEDURE gerar_menus_analise_migr ( cd_funcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_objeto_w		varchar(50);
nr_seq_objeto_w		bigint;
nm_objeto_ww		varchar(50);
nr_seq_objeto_ww	bigint;
ds_objeto_w		varchar(255);
ds_shortcut_w		varchar(255);
nr_seq_apresent_w	bigint;
ie_atalho_w		varchar(15);
ie_ctrl_w		varchar(1);
ie_shift_w		varchar(1);
ie_alt_w		varchar(1);
ie_nulo_w		varchar(1);
ds_shortcut_ww		varchar(255);
nm_objeto_java_w	varchar(50);
nr_seq_obj_menu_w	bigint;

c01 CURSOR FOR
SELECT	a.nm_objeto_pai
from	w_analise_migr_proj a
where	a.cd_funcao = cd_funcao_p
and	a.ie_objeto = 'TMenuItem'
and	not exists (
		SELECT	1
		from	dic_objeto x
		where	x.cd_funcao = a.cd_funcao
		and	x.nm_objeto_orig = a.nm_objeto_pai
		and	x.ie_tipo_objeto = 'M')
group by
	a.nm_objeto_pai;

c02 CURSOR FOR
SELECT	a.nm_objeto,
	a.ds_objeto,
	a.ds_shortcut
from	w_analise_migr_proj a
where	a.cd_funcao = cd_funcao_p
and	a.ie_objeto = 'TMenuItem'
and	a.nm_objeto_pai = nm_objeto_w
and	not exists (
		SELECT	1
		from	dic_objeto x
		where	x.cd_funcao = a.cd_funcao
		and	x.nm_objeto_orig = a.nm_objeto
		and	x.ie_tipo_objeto = 'MI')
order by
	a.nr_sequencia;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into nm_objeto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	coalesce(max(x.nr_sequencia),0)
		into STRICT	nr_seq_obj_menu_w
		from	dic_objeto x
		where	x.cd_funcao = cd_funcao_p
		and	x.nm_objeto_orig = nm_objeto_w
		and	x.ie_tipo_objeto = 'M';

		if (nr_seq_obj_menu_w = 0) then
			begin
			nm_objeto_java_w := ajustar_menu_import_migr(nm_objeto_w);

			select	nextval('dic_objeto_seq')
			into STRICT	nr_seq_objeto_w
			;

			insert into dic_objeto(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_tipo_objeto,
				nm_objeto,
				cd_funcao,
				nm_objeto_orig)
			values (
				nr_seq_objeto_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				'M',
				nm_objeto_java_w,
				cd_funcao_p,
				nm_objeto_w);
			end;
		else
			begin
			nr_seq_objeto_w := nr_seq_obj_menu_w;
			end;
		end if;

		open c02;
		loop
		fetch c02 into	nm_objeto_ww,
				ds_objeto_w,
				ds_shortcut_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			ie_atalho_w	:= null;
			ie_ctrl_w 	:= 'N';
			ie_shift_w	:= 'N';
			ie_alt_w	:= 'N';
			ie_nulo_w	:= 'N';

			if (ds_shortcut_w IS NOT NULL AND ds_shortcut_w::text <> '') then
				begin
				ds_shortcut_ww	:= ds_shortcut_w;
				ds_shortcut_ww	:= replace(upper(ds_shortcut_ww),'CTRL','');
				ds_shortcut_ww	:= replace(upper(ds_shortcut_ww),'SHIFT','');
				ds_shortcut_ww	:= replace(upper(ds_shortcut_ww),'ALT','');
				ds_shortcut_ww	:= replace(upper(ds_shortcut_ww),'+','');
				ie_atalho_w	:= ds_shortcut_ww;
				end;
			end if;
			if (position('CTRL' in upper(ds_shortcut_w)) > 0) then
				begin
				ie_ctrl_w	:= 'S';
				end;
			end if;
			if (position('SHIFT' in upper(ds_shortcut_w)) > 0) then
				begin
				ie_shift_w	:= 'S';
				end;
			end if;
			if (position('ALT' in upper(ds_shortcut_w)) > 0) then
				begin
				ie_alt_w	:= 'S';
				end;
			end if;
			if (ds_objeto_w = '-') then
				begin
				ie_nulo_w	:= 'S';
				end;
			end if;

			nm_objeto_java_w := ajustar_menu_item_import_migr(nm_objeto_ww);

			select	nextval('dic_objeto_seq')
			into STRICT	nr_seq_objeto_ww
			;

			insert into dic_objeto(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_tipo_objeto,
				nm_objeto,
				ds_texto,
				nr_seq_obj_sup,
				cd_funcao,
				nr_seq_apres,
				ie_atalho,
				ie_ctrl,
				ie_shift,
				ie_alt,
				ie_objeto_nulo,
				ie_tipo_obj_menu,
				nm_objeto_orig,
				ds_informacao)
			values (
				nr_seq_objeto_ww,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				'MI',
				nm_objeto_java_w,
				ds_objeto_w,
				nr_seq_objeto_w,
				cd_funcao_p,
				nr_seq_apresent_w,
				ie_atalho_w,
				ie_ctrl_w,
				ie_shift_w,
				ie_alt_w,
				ie_nulo_w,
				'T',
				nm_objeto_ww,
				'Condição de habilitação:' || chr(10) || chr(10) || 'Ação gerada pela utilização:' || chr(10));
			end;
		end loop;
		close c02;
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
