SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_menu_cron_migr_dic_obj3 ( cd_funcao_p bigint, nr_seq_obj_menu_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cronograma_w	bigint;
nr_seq_atividade_w	bigint;
nr_seq_apresent_w	bigint;
nr_seq_atividade_ww	bigint;
nm_objeto_w		varchar(255);

c01 CURSOR FOR
SELECT	a.nm_objeto
from	dic_objeto a
where	a.cd_funcao = cd_funcao_p
and	a.nr_sequencia = nr_seq_obj_menu_item_p
and	a.ie_tipo_objeto = 'MI'
group by
	a.nm_objeto
order by
	a.nm_objeto;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_seq_obj_menu_item_p IS NOT NULL AND nr_seq_obj_menu_item_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(max(c.nr_sequencia),0)
	into STRICT	nr_seq_cronograma_w
	from	proj_cronograma c,
		proj_projeto p
	where	c.nr_seq_proj = p.nr_sequencia
	and	c.ie_tipo_cronograma = 'E'
	and	p.cd_funcao = cd_funcao_p;

	if (nr_seq_cronograma_w > 0) then
		begin
		select	coalesce(min(a.nr_sequencia),0),
			coalesce(max(a.nr_seq_apres),0)
		into STRICT	nr_seq_atividade_w,
			nr_seq_apresent_w
		from	proj_cron_etapa a
		where	a.nr_seq_cronograma = nr_seq_cronograma_w
		and	a.ie_tipo_obj_proj_migr = 'TMenuItem';

		if (nr_seq_atividade_w > 0) then
			begin
			open c01;
			loop
			fetch c01 into nm_objeto_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
				begin
				nr_seq_apresent_w := nr_seq_apresent_w + 10;

				select	nextval('proj_cron_etapa_seq')
				into STRICT	nr_seq_atividade_ww
				;

				insert into proj_cron_etapa(
					nr_seq_cronograma,
					nr_sequencia,
					ds_atividade,
					ie_fase,
					qt_hora_prev,
					pr_etapa,
					nr_seq_apres,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ie_modulo,
					ie_tipo_obj_proj_migr,
					nr_seq_superior,
					ie_atividade_adicional)
				values (
					nr_seq_cronograma_w,
					nr_seq_atividade_ww,
					nm_objeto_w,
					'N',
					1,
					0,
					nr_seq_apresent_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					'N',
					'TMenuItem',
					nr_seq_atividade_w,
					'S');

				CALL atualizar_horas_etapa_cron(nr_seq_atividade_ww);
				end;
			end loop;
			close c01;
			CALL atualizar_horas_etapa_cron(nr_seq_atividade_w);
			end;
		end if;
		CALL atualizar_total_horas_cron(nr_seq_cronograma_w);
		CALL gerar_classif_etapa_proj(nr_seq_cronograma_w, nm_usuario_p);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
