CREATE OR REPLACE PROCEDURE gerar_meses_laudo_sus ( cd_pessoa_fisica_p text, ie_tipo_laudo_apac_p bigint, nr_seq_interno_p bigint, qt_meses_prev_p INOUT bigint, qt_meses_autorizado_p INOUT bigint) AS $body$
DECLARE

 
qt_meses_prev_w		smallint;
qt_meses_autorizado_w	integer;
nr_seq_interno_w	bigint;


BEGIN 
 
select	max(coalesce(b.qt_meses_prev,0)), 
	max(coalesce(b.qt_meses_autorizado,0)) 
into STRICT	qt_meses_prev_w, 
	qt_meses_autorizado_w 
from	sus_laudo_paciente b 
where	nr_seq_interno	= (	SELECT	max(c.nr_seq_interno) 
				from	sus_laudo_paciente c, 
					atendimento_paciente a 
				where	a.nr_atendimento	= c.nr_atendimento 
				and	c.nr_seq_interno	<> nr_seq_interno_p 
				and	c.ie_tipo_laudo_apac	= ie_tipo_laudo_apac_p 
				and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p);
 
qt_meses_prev_p		:= qt_meses_prev_w;
qt_meses_autorizado_p	:= qt_meses_autorizado_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
