CREATE OR REPLACE PROCEDURE gerar_morse_sae (( nr_sequencia_p bigint, nm_usuario_p text) is nr_atendimento_w bigint) RETURNS varchar AS $body$
DECLARE


	ie_retorno_w	varchar(10);

	
BEGIN
	select	max(f.ie_item_morse)
	into STRICT	ie_retorno_w
	from	pe_prescricao a,
		pe_prescr_item_result b,
		pe_item_examinar c,
		pe_item_tipo_item d,
		pe_tipo_item e,
		pe_item_resultado f
	where	a.nr_sequencia	= b.nr_seq_prescr
	and	a.nr_sequencia	= nr_sequencia_p
	and	b.nr_seq_item	= c.nr_sequencia
	and	d.nr_seq_item	= c.nr_sequencia
	and	e.nr_sequencia	= d.nr_seq_tipo_item
	and	c.ie_morse	= ie_morse_p
	and	f.nr_sequencia	= b.nr_seq_result;

	return ie_retorno_w;
	end;


begin
select	max(a.cd_prescritor),
	max(a.nr_atendimento)
into STRICT	cd_profissional_w,
	nr_atendimento_w
from	pe_prescricao a,
	pe_prescr_item_result b
where	a.nr_sequencia = b.nr_seq_prescr
and	a.nr_sequencia = nr_sequencia_p;


select	count(*)
into STRICT	qt_reg_w
from	pe_prescricao a,
	pe_prescr_item_result b,
	pe_item_examinar c,
	pe_item_tipo_item d,
	pe_tipo_item e,
	pe_item_resultado f
where	a.nr_sequencia	= b.nr_seq_prescr
and	a.nr_sequencia	= nr_sequencia_p
and	b.nr_seq_item	= c.nr_sequencia
and	d.nr_seq_item	= c.nr_sequencia
and	e.nr_sequencia	= d.nr_seq_tipo_item
and	(c.ie_morse IS NOT NULL AND c.ie_morse::text <> '')
and	f.nr_sequencia	= b.nr_seq_result;

if (qt_reg_w	> 0) then

	ie_hist_queda_w			:= coalesce(obterValorSAEMorse('HQ'),'N');
	ie_diag_secundario_w		:= coalesce(obterValorSAEMorse('PC'),'N');
	ie_ajuda_deambular_w		:= coalesce(obterValorSAEMorse('AXDEAM'),0);
	ie_uso_heparina_w		:= coalesce(obterValorSAEMorse('PFI'),'N');
	ie_modo_andar_w			:= coalesce(obterValorSAEMorse('CMAR'),0);
	ie_estado_mental_w		:= coalesce(obterValorSAEMorse('EM'),0);

	insert into escala_morse(
					nr_sequencia,
					cd_profissional,
					dt_atualizacao,
					dt_avaliacao,
					ie_hist_queda,
					ie_diag_secundario,
					ie_ajuda_deambular,
					ie_uso_heparina,
					ie_modo_andar,
					ie_estado_mental,
					nm_usuario,
					nr_atendimento,
					ie_situacao,
					dt_liberacao)
		values (		nextval('escala_morse_seq'),
					cd_profissional_w,
					clock_timestamp(),
					clock_timestamp(),
					ie_hist_queda_w,
					ie_diag_secundario_w,
					ie_ajuda_deambular_w,
					ie_uso_heparina_w,
					ie_modo_andar_w,
					ie_estado_mental_w,
					nm_usuario_p,
					nr_atendimento_w,
					'A',
					clock_timestamp());
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
