SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_movimento_inclusao_pac ( nr_sequencia_p bigint, ie_tipo_p text, nm_usuario_p text) AS $body$
DECLARE


nr_interno_conta_w		bigint;
nr_atendimento_w		bigint;
vl_item_w			double precision;
qt_item_w		double precision;
cd_item_w		bigint;
ie_origem_proced_w	bigint;


BEGIN

if (ie_tipo_p = 'M') then

	select	max(nr_interno_conta),
		max(nr_atendimento),
		max(vl_material),
		max(qt_material),
		max(cd_material)
	into STRICT	nr_interno_conta_w,
		nr_atendimento_w,
		vl_item_w,
		qt_item_w,
		cd_item_w
	from	material_atend_paciente
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_tipo_p = 'P') then

	select	max(nr_interno_conta),
		max(nr_atendimento),
		max(vl_procedimento),
		max(qt_procedimento),
		max(cd_procedimento),
		max(ie_origem_proced)
	into STRICT	nr_interno_conta_w,
		nr_atendimento_w,
		vl_item_w,
		qt_item_w,
		cd_item_w,
		ie_origem_proced_w
	from	procedimento_paciente
	where	nr_sequencia = nr_sequencia_p;

end if;

insert into movimento_inclusao_pacote(
	nr_sequencia,
	nr_atendimento,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nr_interno_conta,
	nm_usuario_nrec,
	cd_item,
	ie_origem_proced,
	qt_item,
	ie_tipo_item,
	vl_item)
values (	nextval('movimento_inclusao_pacote_seq'),
	nr_atendimento_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nr_interno_conta_w,
	nm_usuario_p,
	cd_item_w,
	ie_origem_proced_w,
	qt_item_w,
	ie_tipo_p,
	vl_item_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
