CREATE OR REPLACE PROCEDURE gerar_movto_cartao_adiant ( nr_adiantamento_p bigint, nr_seq_bandeira_p bigint, nr_autorizacao_p bigint, vl_transacao_p bigint, ds_comprovante_p text, ie_tipo_cartao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_tipo_cartao_w	varchar(1);
