BEGIN

if (ie_tipo_cartao_p = 0) then
	ie_tipo_cartao_w := 'C';
else
	ie_tipo_cartao_w := 'D';
end if;

if (nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '') then

	select	cd_cgc,
		cd_pessoa_fisica
	into STRICT	cd_cgc_w,
		cd_pessoa_fisica_w
	from	adiantamento
	where	nr_adiantamento = nr_adiantamento_p;

	insert into movto_cartao_cr(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_transacao,
		vl_transacao,
		nr_autorizacao,
		dt_baixa,
		cd_estabelecimento,
		nr_cartao,
		nr_seq_bandeira,
		ie_tipo_cartao,
		nr_seq_caixa_rec,
		ie_lib_caixa,
		nr_seq_trans_caixa,
		nr_seq_forma_pagto,
		ds_comprovante,
		qt_parcela,
		ie_situacao,
		cd_pessoa_fisica,
		cd_cgc,
		nr_adiantamento,
		dt_liberacao,
		dt_cancelamento,
		ds_observacao,
		nr_seq_grupo_prod)
	values (nextval('movto_cartao_cr_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		vl_transacao_p,
		nr_autorizacao_p,
		null,
		cd_estabelecimento_p,
		null,
		nr_seq_bandeira_p,
		ie_tipo_cartao_w,
		null,
		'S',
		null,
		null,
		ds_comprovante_p,
		null,
		'A',
		cd_pessoa_fisica_w,
		cd_cgc_w,
		nr_adiantamento_p,
		null,
		null,
		null,
		null);
end if;

