CREATE OR REPLACE PROCEDURE gerar_movto_cheque_cr (nr_seq_cheque_p bigint, nr_seq_saldo_caixa_p bigint, nr_seq_lote_p bigint, nr_seq_trans_financ_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_caixa_w		bigint;


BEGIN

select	nextval('movto_trans_financ_seq')
into STRICT	nr_sequencia_w
;

select	max(nr_seq_caixa)
into STRICT	nr_seq_caixa_w
from	caixa_saldo_diario
where	nr_sequencia	= nr_seq_saldo_caixa_p;

insert into movto_trans_financ(nr_sequencia,
	dt_transacao,
	dt_referencia_saldo,
	nr_seq_trans_financ,
	vl_transacao,
	nr_seq_saldo_caixa,
	nr_seq_caixa,
	nr_seq_lote,
	nr_seq_cheque,
	nr_lote_contabil,
	ie_conciliacao,
	dt_atualizacao,
	nm_usuario)
SELECT	nr_sequencia_w,
	clock_timestamp(),
	trunc(clock_timestamp(),'dd'),
	nr_seq_trans_financ_p,
	vl_cheque,
	nr_seq_saldo_caixa_p,
	nr_seq_caixa_w,
	nr_seq_lote_p,
	nr_seq_cheque,
	0,
	'N',
	clock_timestamp(),
	nm_usuario
from	cheque_cr
where	nr_seq_cheque	= nr_seq_cheque_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
