SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_movto_cme_externo ( nr_sequencia_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_gerar_lote_w			varchar(1);
ie_gerar_movto_w			varchar(1);
nr_sequencia_w			bigint;
nr_seq_item_w			bigint;
ds_lote_w			varchar(20);
dt_validade_w			timestamp;
cd_material_w			integer;
cd_cnpj_w			varchar(14);
nr_seq_lote_w			bigint;
nr_digito_verif_w			smallint;
nr_movimento_estoque_w		bigint;
cd_local_estoque_w		integer;
dt_mesano_referencia_w		timestamp;
cd_operacao_estoque_w		integer;
cd_unidade_medida_estoque_w	varchar(15);
cd_material_estoque_w		integer;
cd_centro_custo_w		integer;
qt_existe_w			bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.nr_seq_item,
	a.ds_lote,
	a.dt_validade,
	b.cd_cnpj,
	c.cd_material
from	cm_item c,
	cm_envio_externo b,
	cm_envio_externo_item a
where	a.nr_seq_item = c.nr_sequencia
and	a.nr_seq_envio = b.nr_sequencia
and	b.nr_sequencia = nr_sequencia_p
and	(c.cd_material IS NOT NULL AND c.cd_material::text <> '');



BEGIN

/* Procedure inativada/não será mais utilizada pois a movimentação será gerada na nota fiscal */

select	coalesce(max(cd_local_estoque),0)
into STRICT	cd_local_estoque_w
from	setor_atendimento
where	cd_setor_atendimento = cd_setor_atendimento_p;

if (cd_local_estoque_w = 0) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(267198);
	--'Não há local de estoque definido para o setor de atendimento'
end if;

select	'N' --nvl(max(obter_valor_param_usuario(406, 84, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)), 'N')
into STRICT	ie_gerar_lote_w
;

select	'N' --nvl(max(obter_valor_param_usuario(406, 85, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)), 'N')
into STRICT	ie_gerar_movto_w
;

select	0 --nvl(max(obter_valor_param_usuario(406, 86, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)),0)
into STRICT	cd_operacao_estoque_w
;

select	count(*)
into STRICT	qt_existe_w
from	operacao_estoque
where	cd_operacao_estoque = cd_operacao_estoque_w;

if (qt_existe_w = 0) and (ie_gerar_movto_w = 'S') then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(267208);
	--'Operação não encontrada! Verifique parâmetro [86]'
end if;

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	nr_seq_item_w,
	ds_lote_w,
	dt_validade_w,
	cd_cnpj_w,
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	nr_seq_lote_w	:= 0;
	if (ie_gerar_lote_w = 'S') and (ds_lote_w IS NOT NULL AND ds_lote_w::text <> '') then
		begin

		select	nextval('material_lote_fornec_seq')
		into STRICT	nr_seq_lote_w
		;

		nr_digito_verif_w	:= calcula_digito('Modulo11', nr_seq_lote_w);

		insert into material_lote_fornec(
			nr_sequencia,			cd_material,
			nr_digito_verif,			dt_atualizacao,
			nm_usuario,			ds_lote_fornec,
			dt_validade,			cd_cgc_fornec,
			nr_sequencia_nf,			nr_item_nf,
			qt_material,			cd_estabelecimento,
			nr_seq_marca,			ie_origem_lote,
			ie_validade_indeterminada,		ie_situacao,
			nr_nota_fiscal,			cd_barra_material,
			ds_barras,			dt_atualizacao_nrec,
			nm_usuario_nrec,			ds_observacao,
			dt_fabricacao)
		values (	nr_seq_lote_w,			cd_material_w,
			nr_digito_verif_w,			clock_timestamp(),
			nm_usuario_p,			ds_lote_w,
			dt_validade_w,			cd_cnpj_w,
			null,				null,
			1,				cd_estabelecimento_p,
			null,				'C',
			CASE WHEN coalesce(dt_validade_w::text, '') = '' THEN 'S'  ELSE 'N' END ,	'A',
			null,				null,
			null,				clock_timestamp(),
			nm_usuario_p,			null,
			null);

		update	cm_envio_externo_item
		set	nr_seq_lote	= nr_seq_lote_w
		where	nr_sequencia	= nr_sequencia_w;
		end;
	end if;

	if (ie_gerar_movto_w = 'S') then
		begin

		select	nextval('movimento_estoque_seq')
		into STRICT	nr_movimento_estoque_w
		;

		select	dt_mesano_vigente
		into STRICT	dt_mesano_referencia_w
		from	parametro_estoque
		where	cd_estabelecimento = cd_estabelecimento_p;

		select	cd_unidade_medida_estoque,
			cd_material_estoque
		into STRICT	cd_unidade_medida_estoque_w,
			cd_material_estoque_w
		from	material
		where	cd_material = cd_material_w;

		select	coalesce(max(cd_centro_custo), 0)
		into STRICT	cd_centro_custo_w
		from	setor_atendimento
		where	cd_setor_atendimento = cd_setor_atendimento_p;


	       	insert into movimento_estoque(
 			nr_movimento_estoque, 		cd_estabelecimento,
	 		cd_local_estoque,		dt_movimento_estoque,
	 		cd_operacao_estoque, 		cd_acao,
	 		cd_material,	 		dt_mesano_referencia,
	 		qt_movimento,	 		dt_atualizacao,

	 		nm_usuario,	 		ie_origem_documento,
			nr_documento,	 		nr_sequencia_item_docto,
	 		cd_cgc_emitente,		cd_serie_nf,
	 		nr_sequencia_documento,		vl_movimento,
	 		cd_unidade_medida_estoque,	cd_procedimento,
	 		cd_setor_atendimento, 		cd_conta,
	 		dt_contabil,	 		cd_lote_fabricacao,
			dt_validade,			qt_estoque,
	 		dt_processo,	 		cd_local_estoque_destino,
	 		cd_centro_custo,		cd_unidade_med_mov,
	 		nr_movimento_estoque_corresp,	cd_conta_contabil,
	 		cd_material_estoque,		ie_origem_proced,
	 		cd_fornecedor,	 		nr_lote_contabil,
			nr_seq_tab_orig,		nr_seq_lote_fornec)
		values (	nr_movimento_estoque_w,		cd_estabelecimento_p,
			cd_local_estoque_w,		clock_timestamp(),
			cd_operacao_estoque_w,		'1',
			cd_material_w,			dt_mesano_referencia_w,
			1,				clock_timestamp(),
			nm_usuario_p,			'1',
			substr(nr_sequencia_p,1,15),	nr_sequencia_w,
			cd_cnpj_w,			null,
			nr_sequencia_p,			null,
			cd_unidade_medida_estoque_w,	null,
			cd_setor_atendimento_p,		null,
			null,				ds_lote_w,
			dt_validade_w,			1,
			null,				null,
			cd_centro_custo_w,		cd_unidade_medida_estoque_w,
			null,				null,
			cd_material_estoque_w,		null,
			null,				null,
			nr_sequencia_p,			CASE WHEN nr_seq_lote_w=0 THEN  null  ELSE nr_seq_lote_w END );
		end;
	end if;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_movto_cme_externo ( nr_sequencia_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

