CREATE OR REPLACE PROCEDURE gerar_movto_despesa_equip (vl_despesa_p bigint, nr_seq_trans_financ_p bigint, nr_seq_conta_banco_p bigint, dt_baixa_p timestamp, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;


BEGIN

if (nr_seq_conta_banco_p IS NOT NULL AND nr_seq_conta_banco_p::text <> '') and (nr_seq_trans_financ_p IS NOT NULL AND nr_seq_trans_financ_p::text <> '') and (vl_despesa_p <> 0) and (dt_baixa_p IS NOT NULL AND dt_baixa_p::text <> '') then

	select	nextval('movto_trans_financ_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into movto_trans_financ(nr_sequencia,
					dt_transacao,
					nr_seq_trans_financ,
					vl_transacao,
					dt_atualizacao,
					nm_usuario,
					nr_lote_contabil,
					ie_conciliacao,
					nr_seq_banco,
					dt_referencia_saldo)
				values (nr_sequencia_w,
					dt_baixa_p,
					nr_seq_trans_financ_p,
					vl_despesa_p,
					clock_timestamp(),
					nm_usuario_p,
					0,
					'N',
					nr_seq_conta_banco_p,
					clock_timestamp());

end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
