where	a.dt_mesano_referencia	= PKG_DATE_UTILS.ADD_MONTH(dt_mesano_referencia_p,-1,0)
and	((cd_estabelecimento_w = 0) or
	(cd_estabelecimento_w > 0 AND a.cd_estabelecimento	= cd_estabelecimento_w))
