-- DATASOURCE: dbi:Oracle:host=srv-dbora-03.whebdc.com.br;service_name=DEV_1815;port=1521

SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_nf_entrada_transf_quimio ( nr_ordem_compra_p bigint, cd_estabelecimento_p bigint, cd_operacao_nf_p bigint, cd_nat_oper_nf_p bigint, cd_serie_nf_p text, nr_nota_fiscal_p text, cd_local_estoque_p bigint, ds_observacao_p text, nm_usuario_p text, ie_item_nf_p text, cd_setor_atendimento_p bigint, nr_sequencia_p INOUT bigint, ds_erro_p INOUT text, ds_erro_item_p INOUT text, ds_erro_nota_p INOUT text) AS $body$
DECLARE


nr_sequencia_w			bigint;
cd_cgc_destino_w			varchar(14);
cd_estab_origem_w			bigint;
cd_cgc_origem_w			varchar(14);
nr_nota_fiscal_w			varchar(255);
nr_sequencia_nf_w			bigint	:= 9;
dt_emissao_w			timestamp;
dt_entrada_saida_w		timestamp;
cd_condicao_pagto_w		bigint;
vl_frete_w			double precision;
qt_itens_nota_w			bigint;
ds_erro_w			varchar(255) := '';
ds_erro_item_w			varchar(255) := '';
ds_erro_nota_w			varchar(255) := '';

/*Itens*/

nr_item_oci_w			integer;
cd_material_w			integer;
cd_unidade_medida_compra_w	varchar(30);
vl_unitario_item_nf_w		double precision;
pr_descontos_w			double precision;
ds_material_direto_w		varchar(4000);
ds_observacao_item_w		varchar(255);
cd_centro_custo_w			integer;
cd_conta_contabil_w		varchar(20);
pr_desc_financ_w			double precision;
dt_prevista_entrega_w		timestamp;
vl_desconto_oci_w			double precision;
vl_desconto_w			double precision;
nr_seq_conta_financeira_w		bigint;
nr_item_nf_w			integer;
cd_unidade_medida_estoque_w	varchar(30);
qt_prevista_entrega_w		double precision;
qt_material_w			double precision;
qt_item_estoque_w			double precision;
vl_total_item_nf_w			double precision;
vl_liquido_w			double precision;
qt_conv_compra_estoque_w		double precision;
cd_material_estoque_w		integer;
ie_tipo_conta_w			integer;
cd_centro_conta_w			integer;
qt_nota_w			integer;
nr_seq_lote_w			bigint;
ie_calcula_nf_w			varchar(01);

/* Lote */

nr_seq_lote_fornec_w		bigint;
ds_lote_fornec_w			varchar(20);
ds_validade_w			varchar(255);
ds_barra_w			varchar(255);

-- Ordem Código de  BARRAS -> Se precisar usar mais tarde, olhar a procedure GERAR_NF_SAIDA_TRANSFERENCIA;
c00 CURSOR FOR
	SELECT  	cd_material,
		cd_unidade_medida_compra,
		nr_item_oci,
		nr_seq_lote,
		qt_material
	from	ordem_compra_item_cb
	where	nr_ordem_compra 	= nr_ordem_compra_p
	and	ie_atende_recebe	= 'A'
	and	coalesce(nr_seq_nota::text, '') = '';

c01 CURSOR FOR
	SELECT	b.nr_item_oci,
		b.cd_material,
		b.cd_unidade_medida_compra,
		b.vl_unitario_item_nf,
		coalesce(b.pr_desconto,0),
		b.ds_complemento,
		b.ds_observacao,
		coalesce(b.pr_desc_financ,0),
		coalesce(b.vl_desconto,0),
		coalesce(b.qt_item_nf,0),
		b.nr_seq_lote_fornec,
		b.cd_lote_fabricacao,
		to_char(b.dt_validade,'dd/mm/yyyy') ds_validade,
		b.cd_barra_material
	from	nota_fiscal a,
		nota_fiscal_item b
	WHERE  	a.nr_sequencia = b.nr_sequencia
	AND    	a.nr_ordem_compra = nr_ordem_compra_p
	AND    	a.ie_tipo_nota in ('ST', 'SE', 'SF','SD')
	AND    	a.cd_estabelecimento = cd_estab_origem_w
	and	(a.dt_atualizacao_estoque IS NOT NULL AND a.dt_atualizacao_estoque::text <> '')
	order 	by	b.nr_item_oci;

-- Item Código BARRAS -> Se precisar usar mais tarde, olhar a procedure GERAR_NF_SAIDA_TRANSFERENCIA;
c02 CURSOR FOR
	SELECT	nr_seq_lote
	from	ordem_compra_item_cb
	where	cd_material 	= cd_material_w
	and	nr_item_oci	= nr_item_oci_w
	and	nr_ordem_compra	= nr_ordem_compra_p
	and	coalesce(nr_seq_nota::text, '') = '';


BEGIN

dt_emissao_w		:= trunc(clock_timestamp(), 'dd');
dt_entrada_saida_w	:= clock_timestamp();

select	cd_estab_transf,
	cd_condicao_pagamento,
	vl_frete
into STRICT	cd_estab_origem_w,
	cd_condicao_pagto_w,
	vl_frete_w
from	ordem_compra
where	nr_ordem_compra = nr_ordem_compra_p;

select	nextval('nota_fiscal_seq')
into STRICT	nr_sequencia_w
;

select	substr(obter_cgc_estabelecimento(cd_estab_origem_w),1,14),
	substr(obter_cgc_estabelecimento(cd_estabelecimento_p),1,14)
into STRICT	cd_cgc_origem_w, /* CNPJ Estabelecimento EMitente da NF */
	cd_cgc_destino_w /* CNPJ do Estabelecimento que está gerando a nota de entrada */
;

ie_calcula_nf_w	:= substr(coalesce(obter_valor_param_usuario(221, 14, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p),'N'),1,1);

if (coalesce(nr_nota_fiscal_p, '0') = '0') then
	begin
	select	coalesce(max(somente_numero(nr_ultima_nf)), nr_sequencia_w) + 1
	into STRICT	nr_nota_fiscal_w
	from	serie_nota_fiscal
	where	cd_serie_nf 		= cd_serie_nf_p
	and	cd_estabelecimento 	= cd_estabelecimento_p;

	select	count(*)
	into STRICT	qt_nota_w
	from	nota_fiscal
	where	cd_estabelecimento = cd_estabelecimento_p
	and	cd_cgc_emitente 	= cd_cgc_origem_w
	and	cd_serie_nf 	= cd_serie_nf_p
	and	nr_nota_fiscal 	= nr_nota_fiscal_w;

	if (qt_nota_w > 0) then
		select (coalesce(max(somente_numero(nr_nota_fiscal)),'0')+1)
		into STRICT	nr_nota_fiscal_w
		from	nota_fiscal
		where	cd_estabelecimento = cd_estabelecimento_p
		and	cd_cgc_emitente = cd_cgc_origem_w
		and	cd_serie_nf = cd_serie_nf_p;
	end if;
	end;
else
	nr_nota_fiscal_w := nr_nota_fiscal_p;
end if;

insert into nota_fiscal(
	nr_sequencia,		cd_estabelecimento,
	cd_cgc_emitente,		cd_serie_nf,
	nr_nota_fiscal,		nr_sequencia_nf,
	cd_operacao_nf,		dt_emissao,
	dt_entrada_saida,		ie_acao_nf,
	ie_emissao_nf,		ie_tipo_frete,
	vl_mercadoria,		vl_total_nota,
	qt_peso_bruto,		qt_peso_liquido,
	dt_atualizacao,		nm_usuario,
	cd_condicao_pagamento,	cd_cgc,
	cd_pessoa_fisica,		vl_ipi,
	vl_descontos,		vl_frete,
	vl_seguro,		vl_despesa_acessoria,
	ds_observacao,		cd_natureza_operacao,
	vl_desconto_rateio,		ie_situacao,
	nr_interno_conta,		nr_seq_protocolo,
	ds_obs_desconto_nf,	nr_seq_classif_fiscal,
	ie_tipo_nota,		nr_ordem_compra,
	ie_entregue_bloqueto,	cd_setor_digitacao)
values ( nr_sequencia_w,		cd_estabelecimento_p,
	cd_cgc_origem_w,		cd_serie_nf_p,
	nr_nota_fiscal_w,		nr_sequencia_nf_w,
	cd_operacao_nf_p,		dt_emissao_w,
	dt_entrada_saida_w,	'1',
	'0',			'0',
	0,			0,
	0,			0,
	clock_timestamp(),			nm_usuario_p,
	cd_condicao_pagto_w,	cd_cgc_destino_w,
	null,			0,
	0,			vl_frete_w,
	0,			0,
	ds_observacao_p,		cd_nat_oper_nf_p,
	0,			'1',
	null,			null,
	null,			null,
	'EN',			nr_ordem_compra_p,
	'N',			cd_Setor_atendimento_p);

CALL gerar_historico_nota_fiscal(nr_sequencia_w, nm_usuario_p, '17', WHEB_MENSAGEM_PCK.get_texto(281661));

if (ie_item_nf_p = 0) then
	OPEN C01;
	LOOP
	FETCH C01 into
		nr_item_oci_w,
		cd_material_w,
		cd_unidade_medida_compra_w,
		vl_unitario_item_nf_w,
		pr_descontos_w,
		ds_material_direto_w,
		ds_observacao_item_w,
		pr_desc_financ_w,
		vl_desconto_oci_w,
		qt_material_w,
		nr_seq_lote_fornec_w,
		ds_lote_fornec_w,
		ds_validade_w,
		ds_barra_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	coalesce(max(nr_item_nf), 0) + 1
		into STRICT	nr_item_nf_w
		from	nota_fiscal_item
		where	nr_sequencia = nr_sequencia_w;

		select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UME'),1,30) cd_unidade_medida_estoque,
			qt_conv_compra_estoque,
			cd_material_estoque
		into STRICT	cd_unidade_medida_estoque_w,
			qt_conv_compra_estoque_w,
			cd_material_estoque_w
		from	material
		where	cd_material = cd_material_w;

		qt_item_estoque_w := qt_material_w * qt_conv_compra_estoque_w;
		if (cd_unidade_medida_compra_w = cd_unidade_medida_estoque_w) then
			qt_item_estoque_w := qt_material_w;
		end if;

		vl_total_item_nf_w		:= coalesce((qt_material_w * vl_unitario_item_nf_w),0);
		vl_unitario_item_nf_w 	:= coalesce(dividir(vl_total_item_nf_w,qt_material_w),0);
		vl_desconto_w		:= coalesce((dividir((vl_total_item_nf_w * pr_descontos_w), 100) + coalesce(vl_desconto_oci_w,0)),0);
		vl_liquido_w		:= coalesce((vl_total_item_nf_w - vl_desconto_w),0);

		ie_tipo_conta_w	:= 3;
		if (coalesce(cd_centro_custo_w::text, '') = '') then
			ie_tipo_conta_w	:= 2;
		end if;

		SELECT * FROM define_conta_material(
			cd_estabelecimento_p, cd_material_w, ie_tipo_conta_w, 0, 0, 0, 0, 0, 0, 0, cd_local_estoque_p, cd_operacao_nf_p, trunc(clock_timestamp()), cd_conta_contabil_w, cd_centro_conta_w, null) INTO STRICT cd_conta_contabil_w, cd_centro_conta_w;

	insert into nota_fiscal_item(
		nr_sequencia,			cd_estabelecimento,
		cd_cgc_emitente,			cd_serie_nf,
		nr_nota_fiscal,			nr_sequencia_nf,
		nr_item_nf,			cd_natureza_operacao,
		qt_item_nf,			vl_unitario_item_nf,
		vl_total_item_nf,			dt_atualizacao,
		nm_usuario,			vl_frete,
		vl_desconto,			vl_despesa_acessoria,
		cd_material,			cd_local_estoque,
		ds_observacao,			ds_complemento,
		cd_unidade_medida_compra,		qt_item_estoque,
		cd_unidade_medida_estoque,		cd_conta_contabil,
		vl_desconto_rateio,			vl_seguro,
		cd_material_estoque,		nr_ordem_compra,
		vl_liquido,
		pr_desconto,			nr_item_oci,
		dt_entrega_ordem,			nr_seq_conta_financ,
		pr_desc_financ,			nr_seq_lote_fornec,
		cd_lote_fabricacao,			dt_validade,
		cd_sequencia_parametro)
	values (	nr_sequencia_w,			cd_estabelecimento_p,
		cd_cgc_origem_w,			cd_serie_nf_p,
		nr_nota_fiscal_w,			nr_sequencia_nf_w,
		nr_item_nf_w,			cd_nat_oper_nf_p,
		qt_material_w,			vl_unitario_item_nf_w,
		vl_total_item_nf_w,			clock_timestamp(),
		nm_usuario_p, 			coalesce(vl_frete_w,0),
 		coalesce(vl_desconto_w,0),		0,
 		cd_material_w, 			cd_local_estoque_p,
		'',				ds_material_direto_w,
		cd_unidade_medida_compra_w,	qt_item_estoque_w,
		cd_unidade_medida_estoque_w,	cd_conta_contabil_w,
		0,				0,
 		cd_material_estoque_w,		nr_ordem_compra_p,
		vl_liquido_w,
		0,				nr_item_oci_w,
		dt_prevista_entrega_w,		nr_seq_conta_financeira_w,
		0,				nr_seq_lote_fornec_w,
		ds_lote_fornec_w,			ds_validade_w,
		philips_contabil_pck.get_parametro_conta_contabil);
		
	if (ds_barra_w IS NOT NULL AND ds_barra_w::text <> '') then
		begin
		update  nota_fiscal_item
		set	cd_barra_material = ds_barra_w
		where   nr_item_nf = nr_item_nf_w
		and	nr_sequencia = nr_sequencia_w
		and	(cd_material IS NOT NULL AND cd_material::text <> '')
		and     coalesce(ds_barras::text, '') = '';
		end;
	end if;	
	end;
	end loop;
	close c01;
end if;

if (ie_calcula_nf_w = 'S') then
	begin
	CALL atualiza_total_nota_fiscal(nr_sequencia_w,nm_usuario_p);
	CALL gerar_vencimento_nota_fiscal(nr_sequencia_w,nm_usuario_p);
	SELECT * FROM consistir_nota_fiscal(nr_sequencia_w, nm_usuario_p, ds_erro_item_w, ds_erro_nota_w) INTO STRICT ds_erro_item_w, ds_erro_nota_w;
	end;
end if;

select 	count(*)
into STRICT	qt_itens_nota_w
from	nota_fiscal_item
where	nr_sequencia = nr_sequencia_w;

if (qt_itens_nota_w = 0) then
	begin
	delete FROM nota_fiscal where nr_sequencia = nr_sequencia_w;
	nr_sequencia_w	:= 0;
	ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(281662);
	end;
end if;

if (qt_itens_nota_w > 0) and (coalesce(ds_erro_item_w::text, '') = '') and (coalesce(ds_erro_nota_w::text, '') = '') and (coalesce(ds_erro_w::text, '') = '') then
	begin
	if (ie_calcula_nf_w = 'S') then
		CALL Atualizar_Nota_Fiscal(nr_sequencia_w,'I',nm_usuario_p,3);
	end if;
	end;
end if;

if (ds_erro_item_w IS NOT NULL AND ds_erro_item_w::text <> '') or (ds_erro_nota_w IS NOT NULL AND ds_erro_nota_w::text <> '') then
	begin
	delete	FROM nota_fiscal
	where	nr_sequencia = nr_sequencia_w;

	nr_sequencia_w	:= 0;
	end;
end if;	

nr_sequencia_p	:= nr_sequencia_w;
ds_erro_p	:= substr(ds_erro_w,1,255);
ds_erro_item_p	:= substr(ds_erro_item_w,1,255);
ds_erro_nota_p	:= substr(ds_erro_nota_w,1,255);

if (coalesce(ds_erro_p::text, '') = '') and (coalesce(ds_erro_item_p::text, '') = '') and (coalesce(ds_erro_nota_p::text, '') = '') then
	
	if (coalesce(nr_nota_fiscal_p, '0') = '0') and (coalesce(qt_nota_w, 0) = 0) then
		update	serie_nota_fiscal
		set	nr_ultima_nf = nr_nota_fiscal_w
		where	cd_serie_nf = cd_serie_nf_p
		and	cd_estabelecimento = cd_estabelecimento_p;
	end if;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_nf_entrada_transf_quimio ( nr_ordem_compra_p bigint, cd_estabelecimento_p bigint, cd_operacao_nf_p bigint, cd_nat_oper_nf_p bigint, cd_serie_nf_p text, nr_nota_fiscal_p text, cd_local_estoque_p bigint, ds_observacao_p text, nm_usuario_p text, ie_item_nf_p text, cd_setor_atendimento_p bigint, nr_sequencia_p INOUT bigint, ds_erro_p INOUT text, ds_erro_item_p INOUT text, ds_erro_nota_p INOUT text) FROM PUBLIC;

