SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_nom_rc_evol_pac (nr_seq_cabecalho_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_evolucao_w			nom_rc_evolucao.ds_evolucao%type;
ds_texto_w				varchar(32000) := null;
ds_texto_concat_w  		varchar(32000)	:= null;


c_evol_pac CURSOR FOR

SELECT 	a.dt_evolucao dt_evolucao,
		obter_evolucao_paciente_nom(a.cd_evolucao) ds_evolucao
from 	evolucao_paciente a
where   a.ie_evolucao_clinica = 'E'
and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and		coalesce(a.dt_inativacao::text, '') = ''
and		a.qt_caracteres > 0
and		coalesce(a.ds_impressao::text, '') = ''
and 	a.nr_atendimento in (SELECT	x.nr_atendimento
							from	nom_rc_cabecalho x
							where	x.nr_sequencia = nr_seq_cabecalho_p
							and		(x.nr_atendimento IS NOT NULL AND x.nr_atendimento::text <> '')
							
union

							select	y.nr_atendimento
							from	atendimento_paciente y,
									nom_rc_cabecalho x
							where	x.nr_seq_episodio = y.nr_seq_episodio
							and		x.nr_sequencia = nr_seq_cabecalho_p);

BEGIN

delete from nom_rc_evolucao
where nr_seq_cabecalho = nr_seq_cabecalho_p;

for	r_c_evol_pac in c_evol_pac LOOP

	if (coalesce(ds_texto_concat_w::text, '') = '') then
		ds_texto_concat_w :=  obter_data_utc(r_c_evol_pac.dt_evolucao,'NOM_TABLE') || ': ' || remove_html_from_text(r_c_evol_pac.ds_evolucao);
	else
		ds_texto_concat_w :=  chr(13) || chr(10) || obter_data_utc(r_c_evol_pac.dt_evolucao,'NOM_TABLE') || ': ' || remove_formatacao_rtf_html(r_c_evol_pac.ds_evolucao);
	end if;

	if (length(coalesce(ds_texto_w,'') || coalesce(ds_texto_concat_w,'')) < 32000) then
		ds_texto_w	:= ds_texto_w || ds_texto_concat_w;
	end if;

end loop;

if (length(ds_texto_w) > 0) then
	insert into nom_rc_evolucao(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_evolucao,
			nr_seq_cabecalho)
		values (nextval('nom_rc_evolucao_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_texto_w,
			nr_seq_cabecalho_p);
end if;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_nom_rc_evol_pac (nr_seq_cabecalho_p bigint, nm_usuario_p text) FROM PUBLIC;

