SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_nom_rc_plano_tratamento ( nr_seq_cabecalho_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w				atendimento_paciente.nr_atendimento%type;
nr_seq_episodio_w				episodio_paciente.nr_sequencia%type;
ds_tratamento_w					varchar(32000) := null;

ds_plano_tratamento_w			nom_rc_plano_tratamento.ds_plano_tratamento%type;

c_plano_tratamento CURSOR FOR
SELECT 	obter_receita_avaliacao_nom(a.nr_atendimento) ds_plano_tratamento
from 	atendimento_paciente a
where 	a.nr_atendimento in (SELECT	x.nr_atendimento
							from	nom_rc_cabecalho x
							where	x.nr_sequencia = nr_seq_cabecalho_p
							and		(x.nr_atendimento IS NOT NULL AND x.nr_atendimento::text <> '')
							
union

							select	y.nr_atendimento
							from	atendimento_paciente y,
									nom_rc_cabecalho x
							where	x.nr_seq_episodio = y.nr_seq_episodio
							and		x.nr_sequencia = nr_seq_cabecalho_p);


BEGIN

delete 	from nom_rc_plano_tratamento
where	nr_seq_cabecalho = nr_seq_cabecalho_p;

	for	r_c_plano_tratamento in c_plano_tratamento loop

		if (r_c_plano_tratamento.ds_plano_tratamento IS NOT NULL AND r_c_plano_tratamento.ds_plano_tratamento::text <> '') then
		
			ds_plano_tratamento_w	:= r_c_plano_tratamento.ds_plano_tratamento || chr(13) || chr(10);
				
			if (position('\rtf1' in ds_plano_tratamento_w) > 0) or (position('<html xmlns="http://www.w3.org/1999/xhtml">' in ds_plano_tratamento_w) > 0) then
				ds_plano_tratamento_w	:= remove_html_from_text(ds_plano_tratamento_w);
			end if;
				
			if (ds_plano_tratamento_w IS NOT NULL AND ds_plano_tratamento_w::text <> '') and (length(coalesce(ds_tratamento_w,'') || coalesce(ds_plano_tratamento_w,'')) < 32000)then
				ds_tratamento_w := ds_tratamento_w || ds_plano_tratamento_w;
			end if;
			
		end if;
		
	end loop;

	insert into nom_rc_plano_tratamento(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_plano_tratamento,
		nr_seq_cabecalho)
	values (nextval('nom_rc_plano_tratamento_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_tratamento_w,
		nr_seq_cabecalho_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
