SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_nota_clinica_req_parecer ( nr_parecer_p bigint, cd_evolucao_p INOUT bigint) AS $body$
DECLARE

 
cd_evolucao_w			bigint;
ie_relev_resumo_alta_w	varchar(1);

nm_usuario_w			usuario.nm_usuario%type;
ie_tipo_evolucao_w 		usuario.ie_tipo_evolucao%type;
ds_motivo_consulta_w	text;


BEGIN 
 
select	nextval('evolucao_paciente_seq') 
into STRICT	cd_evolucao_w
;
 
select	coalesce(max(ie_relev_resumo_alta),'N') 
into STRICT	ie_relev_resumo_alta_w 
from	tipo_evolucao 
where	cd_tipo_evolucao = 'P';
 
nm_usuario_w := wheb_usuario_pck.get_nm_usuario;
 
select	max(ie_tipo_evolucao) 
into STRICT	ie_tipo_evolucao_w 
from	usuario 
where	nm_usuario = nm_usuario_w;
 
 
Insert into evolucao_paciente( 
	cd_evolucao, 
	dt_evolucao, 
	ie_tipo_evolucao, 
	cd_pessoa_fisica, 
	dt_atualizacao, 
	nm_usuario, 
	nr_atendimento, 
	ds_evolucao, 
	cd_medico, 
	dt_liberacao, 
	ie_evolucao_clinica, 
	cd_setor_atendimento, 
	cd_especialidade, 
	cd_especialidade_medico, 
	cd_medico_parecer, 
	ie_recem_nato, 
	ie_situacao, 
	ie_relev_resumo_alta, 
	ie_nivel_atencao) 
SELECT cd_evolucao_w, 
    clock_timestamp(), 
    ie_tipo_evolucao_w, 
    b.cd_pessoa_fisica, 
    clock_timestamp(), 
    nm_usuario_w, 
    b.nr_atendimento, 
    ' ', 
    b.cd_medico, 
    null, 
    'P', 
    obter_setor_atendimento(b.nr_atendimento), 
    null, 
	  CASE WHEN obter_se_especialidade_medico(b.cd_medico,b.cd_especialidade_dest)='S' THEN b.cd_especialidade_dest  ELSE null END , 
    null, 
    'N', 
    'A', 
	  ie_relev_resumo_alta_w, 
	  wheb_assist_pck.get_nivel_atencao_perfil	   
from  PARECER_MEDICO_REQ b 
where b.nr_parecer = nr_parecer_p;
 
cd_evolucao_p := cd_evolucao_w;
 
commit;
 
Select ds_motivo_consulta 
into STRICT	ds_motivo_consulta_w 
from  PARECER_MEDICO_REQ b 
where  b.nr_parecer = nr_parecer_p;
 
update 	evolucao_paciente 
set		ds_evolucao = ds_motivo_consulta_w 
where 	cd_evolucao = cd_evolucao_w;
 
 
commit;
 
 
CALL liberar_evolucao(cd_evolucao_w,nm_usuario_w);
 
CALL gerar_evolucao_vinculo(nr_parecer_p,cd_evolucao_w,'RP');
 
commit;
 
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
