CREATE OR REPLACE PROCEDURE gerar_nova_validade_lote_forn ( nr_seq_lote_p bigint, nm_usuario_p text, pr_var_validade_p bigint, dt_validade_nova_p INOUT timestamp) AS $body$
DECLARE


qt_dias_validade_w		bigint;
qt_dias_aplicar_w		bigint;
dt_validade_nova_w	timestamp;
dt_atual_w		timestamp;
dt_validade_original_w	timestamp;


BEGIN
dt_validade_nova_p	:= null;

select	dt_validade_original
into STRICT	dt_validade_original_w
from	material_lote_fornec
where	nr_sequencia	= nr_seq_lote_p;

if (coalesce(dt_validade_original_w::text, '') = '') then
	begin
	dt_atual_w	:= trunc(clock_timestamp(),'dd');

	update	material_lote_fornec
	set	dt_validade_original	= dt_validade
	where	nr_sequencia	= nr_seq_lote_p;

	select	coalesce(dt_validade - dt_atual_w,0)
	into STRICT	qt_dias_validade_w
	from	material_lote_fornec
	where	nr_sequencia	= nr_seq_lote_p;

	qt_dias_aplicar_w		:= dividir((qt_dias_validade_w * pr_var_validade_p),100);
	dt_validade_nova_w	:= trunc((dt_atual_w + qt_dias_aplicar_w),'dd');

	update	material_lote_fornec
	set	dt_validade	= dt_validade_nova_w
	where	nr_sequencia	= nr_seq_lote_p;

	dt_validade_nova_p	:= dt_validade_nova_w;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
