CREATE OR REPLACE PROCEDURE gerar_novo_pagador_atendimento (nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	delete	from atendimento_pagador
	where	nr_atendimento = nr_atendimento_p;

	insert into atendimento_pagador(
			nr_sequencia,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_atendimento,
			cd_pessoa_fisica,
			cd_cgc
			)
			values (
			nextval('atendimento_pagador_seq'),
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			nr_atendimento_p,
			cd_pessoa_fisica_p,
			cd_cgc_p
			);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
