CREATE OR REPLACE PROCEDURE gerar_nr_duplicata_receber (nr_titulo_p bigint) AS $body$
DECLARE


cd_estabelecimento_w	smallint;
nr_ultima_w		bigint;


BEGIN

select	max(a.nr_ultima),
	max(a.cd_estabelecimento)
into STRICT	nr_ultima_w,
	cd_estabelecimento_w
from	numero_duplicata_receber a,
	titulo_receber b
where	a.cd_estabelecimento = b.cd_estabelecimento
and	b.nr_titulo = nr_titulo_p;

if (nr_ultima_w IS NOT NULL AND nr_ultima_w::text <> '') then
	nr_ultima_w := nr_ultima_w + 1;

	update	numero_duplicata_receber
	set	nr_ultima = nr_ultima_w
	where	cd_estabelecimento = cd_estabelecimento_w;

	update	titulo_receber
	set	nr_duplicata = nr_ultima_w
	where	nr_titulo = nr_titulo_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
