CREATE OR REPLACE PROCEDURE gerar_nr_sequencial_nf_item ( nr_sequencia_p bigint) AS $body$
DECLARE


nr_item_nf_w	integer;
nr_item_seq_w	integer := 0;

C01 CURSOR FOR
	SELECT	 nr_item_nf
	from	 nota_fiscal_item
	where	 nr_sequencia = nr_sequencia_p
	order by nr_item_nf;


BEGIN

open C01;
loop
fetch C01 into
	nr_item_nf_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	nr_item_seq_w := nr_item_seq_w + 1;

	update	nota_fiscal_item
	set	nr_item_sequencial = nr_item_seq_w
	where	nr_sequencia = nr_sequencia_p
	and	nr_item_nf = nr_item_nf_w;

	end;
end loop;
close C01;

end;
$body$
