CREATE OR REPLACE PROCEDURE gerar_numero_rps_caixa_receb (nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_rps_w	bigint := 0;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	coalesce(max(nr_rps_atual),0)
	into STRICT	nr_rps_w
	from	parametro_tesouraria
	where	cd_estabelecimento = cd_estabelecimento_p;

	update	parametro_tesouraria
	set	nr_rps_atual         = nr_rps_w + 1
	where	cd_estabelecimento   = cd_estabelecimento_p;

	update	caixa_receb
	set	nm_usuario     = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		nr_rps = nr_rps_w + 1
	where	nr_sequencia   = nr_sequencia_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
