CREATE OR REPLACE PROCEDURE gerar_numero_rps (nr_titulo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_rps_w	bigint := 0;


BEGIN
select	coalesce(max(nr_rps_atual),0)
into STRICT	nr_rps_w
from	parametro_tesouraria
where	cd_estabelecimento = cd_estabelecimento_p;

update	titulo_receber
set	nr_rps = nr_rps_w + 1,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_titulo = nr_titulo_p;

update	parametro_tesouraria
set	nr_rps_atual	= nr_rps_w + 1,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	cd_estabelecimento = cd_estabelecimento_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
