CREATE OR REPLACE PROCEDURE gerar_nutriente_gen_alim ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_seq_nutriente_w		bigint;
nr_seq_apresentacao_w		bigint;
ie_Atual_seq_apresent_w		varchar(1);

C01 CURSOR FOR
	SELECT
		nr_sequencia,
		CASE WHEN ie_Atual_seq_apresent_w='S' THEN coalesce(nr_seq_apresentacao,0)  ELSE 0 END
	FROM	nut_nutriente a
	WHERE	ie_situacao	= 'A'
	and	not exists (
		SELECT 1
		from nut_comp_nutricional b
		where b.nr_seq_nutriente 	= a.nr_sequencia
		  and b.nr_seq_gen_alim		= nr_sequencia_p);


BEGIN

select	coalesce(obter_valor_param_usuario(912, 11, Obter_Perfil_Ativo, nm_usuario_p,1),'N')
into STRICT	ie_Atual_seq_apresent_w
;

OPEN C01;
LOOP
	FETCH C01 INTO
		nr_seq_nutriente_w,
		nr_seq_apresentacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	BEGIN
	select nextval('nut_comp_nutricional_seq')
	into STRICT	nr_sequencia_w
	;

	INSERT INTO nut_comp_nutricional(nr_sequencia, nr_seq_nutriente, nr_seq_gen_alim,
		 qt_composicao, dt_atualizacao, nm_usuario, ie_informacao,nr_seq_apresent)
	VALUES (nr_sequencia_w, nr_seq_nutriente_w, nr_sequencia_p,
		 0, clock_timestamp(), nm_usuario_p,'S', nr_seq_apresentacao_w);
	END;
END LOOP;
CLOSE C01;
COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
