SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_nut_prescricao (nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w				bigint;
nr_atendimento_w			bigint;
dt_prescricao_w				timestamp;
cd_setor_atendimento_w			bigint;
dt_inicio_prescricao_w			timestamp;
dt_validade_prescr_w			timestamp;
nr_horas_validade_w			bigint;
nr_seq_dieta_w				bigint;
cd_dieta_w				bigint;
nr_sequencia_prescr_w			bigint;
qt_reg_w				bigint;

c01 CURSOR FOR
	SELECT	b.nr_sequencia,
		b.cd_dieta
	from	prescr_dieta b,
		prescr_medica k
	where	k.nr_prescricao = nr_prescricao_p
	and	k.nr_prescricao	= b.nr_prescricao
	and	coalesce(k.dt_suspensao::text, '') = ''
	and	coalesce(b.ie_suspenso,'N') <> 'S'
	order by b.nr_sequencia;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	prescr_dieta
where	nr_prescricao	= nr_prescricao_p;

if (qt_reg_w	<> 0) then
	begin

	select	a.nr_atendimento,
		a.dt_prescricao,
		a.cd_setor_atendimento,
		a.dt_inicio_prescr,
		a.dt_validade_prescr,
		a.nr_horas_validade
	into STRICT	nr_atendimento_w,
		dt_prescricao_w,
		cd_setor_atendimento_w,
		dt_inicio_prescricao_w,
		dt_validade_prescr_w,
		nr_horas_validade_w
	from	prescr_medica a
	where	a.nr_prescricao		= nr_prescricao_p;


	select	nextval('nut_prescricao_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into nut_prescricao(nr_sequencia,
		nr_atendimento,
		nr_prescricao,
		dt_prescricao,
		cd_setor_atendimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_validade_inicial,
		dt_validade_final,
		nr_horas_validade,
		ie_status_prescricao)
	values (nr_sequencia_w,
		nr_atendimento_w,
		nr_prescricao_p,
		dt_prescricao_w,
		cd_setor_atendimento_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		dt_inicio_prescricao_w,
		dt_validade_prescr_w,
		nr_horas_validade_w,
		'G');

	open C01;
	loop
	fetch C01 into
		nr_sequencia_prescr_w,
		cd_dieta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	nextval('nut_prescr_dieta_seq')
		into STRICT	nr_seq_dieta_w
		;

		insert	into nut_prescr_dieta(NR_SEQUENCIA,
			NR_NUT_PRESCRICAO,
			NR_PRESCRICAO,
			NR_SEQ_PRESCRICAO,
			DT_ATUALIZACAO,
			NM_USUARIO,
			DT_ATUALIZACAO_NREC,
			NM_USUARIO_NREC,
			CD_DIETA_MEDICO,
			CD_DIETA_NUTRICAO)
		values (nr_seq_dieta_w,
			nr_sequencia_w,
			nr_prescricao_p,
			nr_sequencia_prescr_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_dieta_w,
			null);


		end;
	end loop;
	close C01;

	end;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
