CREATE OR REPLACE PROCEDURE gerar_obs_leito_isolado ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, cd_motivo_isolamento_p text, nm_usuario_p text) AS $body$
