SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_obs_processo_aprov ( nr_sequencia_p bigint, ds_observacao_p text, ie_envia_comunic_p text, nm_usuario_p text) AS $body$
DECLARE


nr_solic_compra_w		bigint;
nr_ordem_compra_w		bigint;
nr_cot_compra_w			cot_compra.nr_cot_compra%type;
nm_usuario_comprador_w		varchar(15);
nr_seq_classif_w		bigint;
ds_documento_w			varchar(100);
ds_observacao_w			varchar(2000);
ds_observacao_ww		varchar(4000);


BEGIN

select	substr(max(CASE WHEN coalesce(ds_observacao::text, '') = '' THEN nm_usuario_p || ': '  ELSE ds_observacao || chr(13) || chr(10) || nm_usuario_p || ': ' END ),1,2000)
into STRICT	ds_observacao_w
from 	processo_aprov_compra
where	nr_sequencia = nr_sequencia_p;

ds_observacao_ww := substr(ds_observacao_w || ds_observacao_p,1,4000);

update	processo_aprov_compra
set	ds_observacao = substr(ds_observacao_ww,1,2000)
where	nr_sequencia = nr_sequencia_p;

select	coalesce(max(nr_solic_compra),0)
into STRICT	nr_solic_compra_w
from	solic_compra_item
where	nr_seq_aprovacao = nr_sequencia_p;

select	coalesce(max(nr_ordem_compra),0)
into STRICT	nr_ordem_compra_w
from	ordem_compra_item
where	nr_seq_aprovacao = nr_sequencia_p;

if (ie_envia_comunic_p = 'S') then
	if (nr_solic_compra_w > 0) then

		select	obter_usuario_pf(cd_comprador_resp)
		into STRICT	nm_usuario_comprador_w
		from	solic_compra
		where	nr_solic_compra = nr_solic_compra_w;
		ds_documento_w := WHEB_MENSAGEM_PCK.get_texto(279610) || nr_solic_compra_w;
	elsif (nr_ordem_compra_w > 0) then

		select	obter_usuario_pf(cd_comprador)
		into STRICT	nm_usuario_comprador_w
		from	ordem_compra
		where	nr_ordem_compra = nr_ordem_compra_w;
		ds_documento_w := WHEB_MENSAGEM_PCK.get_texto(279611) || nr_ordem_compra_w;
	else

		select	coalesce(max(nr_cot_compra),0)
		into STRICT	nr_cot_compra_w
		from	cot_compra_item
		where	nr_seq_aprovacao = nr_sequencia_p;

		if (nr_cot_compra_w > 0) then
			select	obter_usuario_pf(cd_comprador)
			into STRICT	nm_usuario_comprador_w
			from	cot_compra
			where	nr_cot_compra = nr_cot_compra_w;
			ds_documento_w := WHEB_MENSAGEM_PCK.get_texto(279612) || nr_cot_compra_w;
		end if;
	end if;

	if (nm_usuario_comprador_w IS NOT NULL AND nm_usuario_comprador_w::text <> '') then

		select	obter_classif_comunic('F')
		into STRICT	nr_seq_classif_w
		;

		insert	into comunic_interna(
			dt_comunicado,
			ds_titulo,
			ds_comunicado,
			nm_usuario,
			dt_atualizacao,
			ie_geral,
			nm_usuario_destino,
			nr_sequencia,
			ie_gerencial,
			nr_seq_classif,
			dt_liberacao)
		values (	clock_timestamp(),
			WHEB_MENSAGEM_PCK.get_texto(279613),
			substr(WHEB_MENSAGEM_PCK.get_texto(279615) || ds_documento_w ||'.' || chr(13) || chr(10) || ds_observacao_p,1,4000),
			nm_usuario_p,
			clock_timestamp(),
			'N',
			nm_usuario_comprador_w,
			nextval('comunic_interna_seq'),
			'N',
			nr_seq_classif_w,
			clock_timestamp());
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_obs_processo_aprov ( nr_sequencia_p bigint, ds_observacao_p text, ie_envia_comunic_p text, nm_usuario_p text) FROM PUBLIC;

