CREATE OR REPLACE PROCEDURE gerar_obs_tecnica_setor ( cd_setor_atendimento_p bigint, nm_usuario_p text, dt_dieta_p timestamp, cd_refeicao_p text, qt_horas_p bigint) AS $body$
DECLARE

 
 
cd_setor_atendimento_w	integer;
ie_permite_rec_nasc_w	varchar(1);
ie_atualizar_obs_nula_w varchar(1);
vl_parametro_w		varchar(5);
c01 CURSOR FOR
 
SELECT	a.cd_setor_atendimento 
from 	setor_atendimento c,	 
	unidade_atendimento a 
where 	c.cd_setor_atendimento = a.cd_setor_atendimento 
and 	c.cd_classif_setor = '9' 
and 	((a.cd_setor_atendimento = cd_setor_atendimento_p) or 
	((cd_setor_atendimento_p = 0) and ((cd_classif_setor in (3,4,8)) or (ie_permite_rec_nasc_w = 'S' and cd_classif_setor = 9)))) 

union all
 
SELECT	a.cd_setor_atendimento	 
from 	unidade_atendimento a 
where 	a.ie_status_unidade = 'P' 
and 	((a.cd_setor_atendimento = cd_setor_atendimento_p) or ((cd_setor_atendimento_p = 0) and ((obter_classif_setor(a.cd_setor_atendimento) in (3,4,8)) or (ie_permite_rec_nasc_w = 'S' and obter_classif_setor(a.cd_setor_atendimento) = 9)))) 

union
 
select	a.cd_setor_atendimento 
from 	unidade_atendimento a 
where 	a.ie_status_unidade = 'M' 
 and ((a.cd_setor_atendimento = cd_setor_atendimento_p) or ((cd_setor_atendimento_p = 0) and ((obter_classif_setor(a.cd_setor_atendimento) in (3,4,8)) or (ie_permite_rec_nasc_w = 'S' and obter_classif_setor(a.cd_setor_atendimento) = 9)))) 
order by 1;


BEGIN 
ie_permite_rec_nasc_w := obter_param_usuario(1000, 26, obter_perfil_ativo, nm_usuario_p, 0, ie_permite_rec_nasc_w);
ie_atualizar_obs_nula_w := obter_param_usuario(1000, 38, obter_perfil_ativo, nm_usuario_p, 0, ie_atualizar_obs_nula_w);
vl_parametro_w	:= coalesce(obter_valor_param_usuario(1000, 8, obter_perfil_ativo, nm_usuario_p,1),'N');
 
open C01;
loop 
fetch C01 into	 
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if	((vl_parametro_w in ('S','U')) and (qt_horas_p > 0)) or (vl_parametro_w in ('A','P','PU','IP','IPU')) then 
		CALL gerar_observacao_tecnica(cd_setor_atendimento_w, dt_dieta_p, cd_refeicao_p, vl_parametro_w, ie_atualizar_obs_nula_w, nm_usuario_p);
	end if;	
	 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
