CREATE OR REPLACE PROCEDURE gerar_obs_tec_ref_setores ( cd_setor_p bigint, cd_dominio_p bigint, qt_hora_p bigint, dt_dieta_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
cd_refeicao_w		varchar(15);

C01 CURSOR FOR 
	SELECT	vl_dominio cd_refeicao 
	from	valor_dominio 
	where	cd_dominio = cd_dominio_p;


BEGIN 
if (cd_dominio_p IS NOT NULL AND cd_dominio_p::text <> '')	and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '')	then 
	begin 
	open C01;
	loop 
	fetch C01 into	 
		cd_refeicao_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL gerar_obs_tecnica_setor(cd_setor_p, nm_usuario_p, dt_dieta_p, cd_refeicao_w, qt_hora_p);
		--obter_dieta_prescricao(cd_setor_p, qt_hora_p, dt_dieta_p, cd_refeicao_w, nm_usuario_p); 
		end;
	end loop;
	close C01;
	end;
end if;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
