CREATE OR REPLACE PROCEDURE gerar_ordem_agecol_usuario (nm_usuario_p text) AS $body$
DECLARE


cd_agenda_w	bigint;
nr_seq_apres_w	bigint;

C01 CURSOR FOR
	SELECT	cd_agenda
	from	agenda_cons_col_ordem
	where	nm_usuario = nm_usuario_p
	order by vl_left;


BEGIN

delete	FROM med_ordem_agenda
where	nm_usuario_nrec = nm_usuario_p;

nr_seq_apres_w	:= 1;

open C01;
loop
fetch C01 into
	cd_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into med_ordem_agenda(
				nr_sequencia,
				cd_agenda,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_apres
				)
		values (
				nextval('med_ordem_agenda_seq'),
				cd_agenda_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_apres_w
				);
	nr_seq_apres_w := nr_seq_apres_w + 1;

	end;
end loop;
close C01;

delete	FROM agenda_cons_col_ordem
where	nm_usuario = nm_usuario_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
