SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_ordem_compra_consig ( cd_estabelecimento_p bigint, cd_fornecedor_consignado_p text, cd_material_p bigint, qt_total_dispensar_p bigint, dt_atualizacao_p timestamp, nm_usuario_p text, nr_prescricao_p bigint, nr_seq_prescricao_p bigint, nr_atendimento_p bigint, cd_local_estoque_p bigint, ds_observacao_p text, cd_local_entrega_p bigint, cd_centro_custo_p bigint, pr_desconto_p bigint, nr_serie_material_p text, cd_pessoa_solicitante_p text, nr_seq_lote_fornec_p bigint, cd_setor_atendimento_p bigint, ie_gera_oc_reposicao_p text, nr_seq_matpaci_p bigint, nr_ordem_compra_gerada_p INOUT bigint, nr_item_oci_gerado_p INOUT bigint) AS $body$
DECLARE



nr_item_oci_w				integer;
cd_comprador_padrao_w			varchar(10);
cd_moeda_padrao_w			bigint;
cd_condicao_pagamento_padrao_w  	bigint;
cd_local_estoque_setor_w		bigint;
cd_local_estoque_padrao_w		bigint;
cd_local_consig_w			bigint;
cd_pessoa_solic_padrao_w        	varchar(10);
qt_dia_prazo_entrega_w			bigint;
cd_estabelecimento_w        		smallint    	:= 1;
qt_compra_w         	    		double precision 	:= 0;
qt_conv_compra_estoque_w    		double precision 	:= 0;
cd_unidade_medida_compra_w  		varchar(30)  	:= '';
qt_conv_estoque_consumo_w		double precision	:= 0;
cd_pessoa_fisica_w  	    		varchar(10) 	:= '';
nr_ordem_compra_w			bigint;
ds_observacao_w				ordem_compra.ds_observacao%type := '';
ds_obs_nota_consumo_w			nota_fiscal.ds_observacao%type	:= '';
dt_prevista_entrega_w			timestamp;
nm_paciente_w				pessoa_fisica.nm_pessoa_fisica%type;
nm_medico_w				varchar(60)	:= '';
cd_convenio_w				integer;
ds_convenio_w				varchar(255)	:= '';
cd_categoria_w				varchar(10);
nr_atendimento_w			bigint	:= '';
cd_local_estoque_w			integer	:= '';
cd_local_entrega_w			integer	:= '';
cd_usuario_convenio_w			varchar(30);
nr_doc_convenio_w			varchar(20);
cd_centro_custo_w			integer	:= '';
cd_centro_custo_setor_w			integer;
dt_cirurgia_w				timestamp;
ds_compl_ordem_consig_w			varchar(255);
vl_unitario_material_w			double precision	:= 0;
nr_cirurgia_w				bigint;
nr_cirurgia_ww				bigint;
nr_seq_agenda_w				agenda_paciente.nr_sequencia%type;
dt_entrega_w				timestamp;
ie_aprova_auto_w			varchar(01);
cd_senha_w				varchar(20);
ds_observacao_item_w			varchar(255);
ie_tipo_conta_w				integer;
cd_conta_contabil_w			varchar(20);
nr_crm_w				varchar(20);
dt_validade_carteira_w			timestamp;
dt_validade_lote_w			timestamp;
ie_centro_custo_consig_w		varchar(1);
nm_pessoa_contato_w			varchar(255);
tx_desc_ordem_w				double precision;
pr_desc_financeiro_w			double precision;
cd_procedimento_w			bigint;
ie_agrupa_oc_novo_item_w		varchar(1);
ie_solic_comprador_w			varchar(1);
nr_item_oci_ww				integer;
nr_seq_entrega_w			bigint;
ie_atualizou_item_w			varchar(1) := 'N';
qt_existe_w				bigint;
ie_preco_sus_oc_w			varchar(1);
ie_tipo_atendimento_w			smallint;
ds_lote_w				varchar(25);
ie_erro_email_w				varchar(1);
ie_avisa_consumo_w			varchar(1);
nr_seq_marca_consig_w			bigint;
nr_seq_grupo_w				bigint;
dt_validade_w				timestamp;
pr_desc_financ_w			double precision;
ie_obs_oc_cirurgia_w			varchar(15);
ie_replicar_desc_financ_item_w		varchar(15);
qt_nota_fiscal_obs_w			integer;
nr_seq_nf_consumo_w			nota_fiscal.nr_sequencia%type;
nr_seq_nf_entrada_w			nota_fiscal.nr_sequencia%type;
nr_nf_vinculada_w			nota_fiscal.nr_nota_fiscal%type;
ds_obs_nf_ordem_w			nota_fiscal.ds_observacao%type;
ds_setor_w				setor_atendimento.ds_setor_atendimento%type;
cd_local_fatur_direto_w			local_estoque.cd_local_estoque%type;
ie_local_fatur_direto_w			varchar(1);
nr_serie_material_w			ordem_compra_item.nr_serie_material%type;
nr_cpf_w				pessoa_fisica.nr_cpf%type;
dt_nascimento_w				timestamp;
ie_obs_nm_consig_w		parametro_compras.ie_obs_nm_consig%type;


BEGIN

cd_estabelecimento_w	:= coalesce(cd_estabelecimento_p,1);

select	obter_valor_param_usuario(917, 130, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w)
into STRICT	ie_replicar_desc_financ_item_w
;

select	coalesce(cd_comprador_consig, cd_comprador_padrao),
      	cd_moeda_padrao,
      	cd_condicao_pagamento_padrao,
      	cd_local_estoque_padrao,
	cd_local_consig,
      	coalesce(cd_pessoa_solic_consig,cd_pessoa_solic_padrao),
	ds_compl_ordem_consig,
	ie_aprova_ordem_consig,
	coalesce(cd_local_entrega_consig, cd_local_entrega_p),
	coalesce(ie_agrupa_oc_novo_item,'N'),
	coalesce(ie_solic_comprador,'N'),
	coalesce(ie_obs_oc_cirurgia,'B'),
	cd_local_fatur_direto,
	coalesce(ie_centro_custo_consig,'A'),
	coalesce(ie_preco_sus_oc,'N')
into STRICT  	cd_comprador_padrao_w,
      	cd_moeda_padrao_w,
      	cd_condicao_pagamento_padrao_w,
      	cd_local_estoque_padrao_w,
	cd_local_consig_w,
      	cd_pessoa_solic_padrao_w,
	ds_compl_ordem_consig_w,
	ie_aprova_auto_w,
	cd_local_entrega_w,
	ie_agrupa_oc_novo_item_w,
	ie_solic_comprador_w,
	ie_obs_oc_cirurgia_w,
	cd_local_fatur_direto_w,
	ie_centro_custo_consig_w,
	ie_preco_sus_oc_w
from  	parametro_compras
where 	cd_estabelecimento = cd_estabelecimento_w;

if (coalesce(cd_pessoa_solic_padrao_w::text, '') = '') or (coalesce(cd_moeda_padrao_w::text, '') = '') or (coalesce(cd_local_estoque_padrao_w::text, '') = '') or (coalesce(cd_comprador_padrao_w::text, '') = '') then
	--(-20011,'Os parametros de compra estao incompletos');
	CALL wheb_mensagem_pck.exibir_mensagem_abort(195748);
end if;

select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMC'),1,30) cd_unidade_medida_compra,
	qt_conv_compra_estoque,
	qt_conv_estoque_consumo
into STRICT 	cd_unidade_medida_compra_w,
	qt_conv_compra_estoque_w,
	qt_conv_estoque_consumo_w
from 	material
where 	cd_material = coalesce(cd_material_p,0);

/* Elemar 25/09/02 - Converter qtde a comprar pela conversao compra/estoque */

qt_compra_w	:= (qt_total_dispensar_p / qt_conv_compra_estoque_w / qt_conv_estoque_consumo_w);

select	coalesce(obter_dados_pf_pj_estab(cd_estabelecimento_w, null, cd_fornecedor_consignado_p, 'EPE'),0),
	coalesce(obter_dados_pf_pj_estab(cd_estabelecimento_w, null, cd_fornecedor_consignado_p, 'ECP'), cd_condicao_pagamento_padrao_w)
into STRICT	qt_dia_prazo_entrega_w,
	cd_condicao_pagamento_padrao_w
;

dt_entrega_w	:= PKG_DATE_UTILS.start_of(clock_timestamp(), 'dd', 0) + qt_dia_prazo_entrega_w;

/* Marcus 09/08/2003 - obter local e centro de custo independente de existir OC */

select	max(s.cd_local_estoque),
	max(s.cd_centro_custo)
into STRICT	cd_local_estoque_setor_w,
	cd_centro_custo_setor_w
FROM prescr_medica a
LEFT OUTER JOIN setor_atendimento s ON (a.cd_setor_atendimento = s.cd_setor_atendimento)
WHERE a.nr_prescricao = nr_prescricao_p;
/* Ordem local_estoque = Parametro consignado, do programa chamador, setor_paciente, parametro padrao */

cd_local_estoque_w	:= coalesce(cd_local_consig_w, cd_local_estoque_p);
cd_local_estoque_w	:= coalesce(cd_local_estoque_w, cd_local_estoque_setor_w);
cd_local_estoque_w	:= coalesce(cd_local_estoque_w, cd_local_estoque_Padrao_w);

if (ie_preco_sus_oc_w = 'S') and (nr_atendimento_p > 0) then

	select	Obter_Dados_Atendimento(nr_atendimento_p, 'TC')
	into STRICT	ie_tipo_atendimento_w
	;
end if;

if (ie_centro_custo_consig_w = 'A') then /*Gerar com o centro do setor do atendimento*/
	select	max(cd_centro_custo)
	into STRICT	cd_centro_custo_w
	from	setor_atendimento
	where	cd_setor_atendimento	= (obter_setor_atendimento(nr_atendimento_p));
	
elsif (ie_centro_custo_consig_w = 'C') then /*Gerar com o centro do setor do consumo*/
	
	select	max(cd_centro_custo)
	into STRICT	cd_centro_custo_w
	from	setor_atendimento
	where	cd_setor_atendimento	= cd_setor_atendimento_p;
	
elsif (ie_centro_custo_consig_w = 'R') then /*Gerar com o centro de custo da regra de ordem consignado*/
	
	cd_centro_custo_w		:= cd_centro_custo_p;
	
elsif (ie_centro_custo_consig_w = 'L') and	 /*Gerar com o centro de custo do local de estoque de consumo*/
	(cd_local_estoque_w IS NOT NULL AND cd_local_estoque_w::text <> '') then
	
	select	max(cd_centro_custo)
	into STRICT	cd_centro_custo_w
	from	local_estoque
	where	cd_local_estoque	= cd_local_estoque_w;

elsif (ie_centro_custo_consig_w = 'S') then	/*Gerar com o centro de custo do setor da prescricao*/
	
	select	max(cd_centro_custo)
	into STRICT	cd_centro_custo_w
	FROM prescr_medica a
LEFT OUTER JOIN setor_atendimento s ON (a.cd_setor_atendimento = s.cd_setor_atendimento)
WHERE a.nr_prescricao = nr_prescricao_p;
	
end if;

/*Como padrao, se o centro de custo e vazio, pega do setor da prescricao*/

if (ie_centro_custo_consig_w <> 'N') and (coalesce(cd_centro_custo_w::text, '') = '') then
	cd_centro_custo_w := cd_centro_custo_setor_w;
end if;

/* Marcus 19/02/2004 permitir informar compradores e solicitantes diferentes por local de estoque */

if (cd_local_estoque_w IS NOT NULL AND cd_local_estoque_w::text <> '') then
	select	coalesce(cd_comprador_consig, cd_comprador_padrao_w),
		coalesce(cd_pessoa_solic_consig, cd_pessoa_solic_padrao_w)
	into STRICT	cd_comprador_padrao_w,
		cd_pessoa_solic_padrao_w
	from	local_estoque
	where	cd_local_estoque	= cd_local_estoque_w;
end if;

if (ie_solic_comprador_w = 'S') then
	begin
	select	coalesce(max(cd_pessoa_fisica),cd_comprador_padrao_w)
	into STRICT	cd_comprador_padrao_w
	from	comprador
	where	cd_pessoa_fisica = coalesce(cd_pessoa_solicitante_p,cd_pessoa_solic_padrao_w);
	end;
elsif (ie_solic_comprador_w = 'G') and (coalesce(cd_material_p,0) > 0) then
	begin
	nr_seq_grupo_w := obter_grupo_compras_material(cd_material_p);
	if (coalesce(nr_seq_grupo_w,0) > 0) then
		select	coalesce(max(cd_comprador),cd_comprador_padrao_w)
		into STRICT	cd_comprador_padrao_w
		from	sup_grupo_compra
		where	nr_sequencia = nr_seq_grupo_w;
	end if;
	end;
end if;

select	coalesce(obter_ordem_atend_consignado(cd_estabelecimento_p, cd_fornecedor_consignado_p, cd_local_entrega_w, 0, nr_atendimento_p, nr_prescricao_p,'G'),0)
into STRICT	nr_ordem_compra_w
;

select	substr(obter_dados_pf_pj_estab(cd_estabelecimento_w, null, cd_fornecedor_consignado_p, 'ENC'),1,255),
	obter_dados_pf_pj_estab(cd_estabelecimento_w, null, cd_fornecedor_consignado_p, 'TDO'),
	obter_dados_pf_pj_estab(cd_estabelecimento_w, null, cd_fornecedor_consignado_p, 'TDF')
into STRICT	nm_pessoa_contato_w,
	tx_desc_ordem_w,
	pr_desc_financeiro_w
;

if (nr_ordem_compra_w = 0) then
        begin
		select	coalesce(max(ie_obs_nm_consig), 'N')
		into STRICT	ie_obs_nm_consig_w
		from	parametro_compras
		where	cd_estabelecimento 	=	cd_estabelecimento_p;
		
	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
		begin
		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_w
		from	cirurgia
		where	nr_prescricao		= nr_prescricao_p;

		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			update	prescr_medica
			set	cd_pessoa_fisica	=	cd_pessoa_fisica_w
			where	nr_prescricao		=	nr_prescricao_p
			and	coalesce(cd_pessoa_fisica::text, '') = '';
		end if;

		select 	max(SUBSTR(OBTER_NOME_PF(m.CD_PESSOA_FISICA), 0, 40)),
			OBTER_SE_INICIAIS_PACIENTE(max(SUBSTR(OBTER_NOME_PF(p.CD_PESSOA_FISICA), 0, 40)), ie_obs_nm_consig_w),
			max(a.nr_atendimento),
			max(a.nr_cirurgia),
			max(p.nr_cpf),
			max(p.dt_nascimento)
		into STRICT	nm_medico_w,
			nm_paciente_w,
			nr_atendimento_w,
			nr_cirurgia_w,
			nr_cpf_w,
			dt_nascimento_w
		from 	pessoa_fisica m,
			Pessoa_fisica p,
			prescr_medica a
		where 	a.nr_prescricao 	= nr_prescricao_p
		  and	a.cd_pessoa_fisica	= p.cd_pessoa_fisica
		  and	a.cd_medico		= m.cd_pessoa_fisica;

		/* Marcus 07/01/2005 - Tratamento da cirurgia pela tabela cirurgia ou prescricao */

		if (coalesce(nr_cirurgia_w::text, '') = '') then
			select	max(nr_cirurgia),
				max(cd_pessoa_fisica)
			into STRICT	nr_cirurgia_w,
				cd_pessoa_fisica_w
			from	cirurgia
			where	nr_prescricao		= nr_prescricao_p;
		end if;

		/* Ricardo 07/04/2004 - Incluido o nr do atendimento na observacao abaixo a pedido do Wander Os 7185 */

		if (ie_obs_nm_consig_w = 'S') then
			ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(301950,'NM_PACIENTE_W='||nm_paciente_w||';NR_ATENDIMENTO_W='||nr_atendimento_w||';NM_MEDICO_W='||nm_medico_w),1,4000);
		else
			ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(1203966,'NR_ATENDIMENTO_W='||nr_atendimento_w||';NM_MEDICO_W='||nm_medico_w),1,4000);
		end if;

		if (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') then
			select	max(PKG_DATE_UTILS.start_of(coalesce(a.dt_inicio_real, a.dt_inicio_prevista), 'dd', 0))
			into STRICT	dt_cirurgia_w
			from	cirurgia a
			where	a.nr_cirurgia		= nr_cirurgia_w;
			ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(301952,'DS_OBSERVACAO_W='||ds_observacao_w||';DT_CIRURGIA_W='|| PKG_DATE_FORMATERS.TO_VARCHAR(dt_cirurgia_w, 'shortDate', cd_estabelecimento_p, nm_usuario_p)),1,4000);
		end if;

		if (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') then
			select	max(cd_procedimento_princ)
			into STRICT	cd_procedimento_w
			from	cirurgia a
			where	a.nr_cirurgia		= nr_cirurgia_w;
			if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then
				ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(301953,'DS_OBSERVACAO_W='||ds_observacao_w||';CD_PROCEDIMENTO_W='||  to_char(cd_procedimento_w)),1,4000);
			end if;
		end if;

		select	coalesce(max(a.nr_sequencia),0)
		into STRICT	nr_seq_agenda_w
		from	agenda_paciente a,
			cirurgia b
		where	a.nr_cirurgia	= b.nr_cirurgia
		and	b.nr_prescricao	= nr_prescricao_p;
		end;

		if (nr_seq_agenda_w = 0) then
			select	coalesce(max(a.nr_sequencia),0)
			into STRICT	nr_seq_agenda_w
			from	agenda_paciente a,
				cirurgia b
			where	a.nr_cirurgia	= b.nr_cirurgia
			and	b.nr_cirurgia	= nr_cirurgia_w;
		end if;
	else
		begin
		select 	b.nr_atendimento,
			p.nr_cpf,
			p.dt_nascimento
		into STRICT	nr_atendimento_w,
			nr_cpf_w,
			dt_nascimento_w
		from 	Pessoa_fisica p,
			Atendimento_Paciente b
		where	b.cd_pessoa_fisica	= p.cd_pessoa_fisica
		and	b.nr_atendimento	= nr_atendimento_p;
		
		if (ie_obs_nm_consig_w = 'S') then
			ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(301950,'NM_PACIENTE_W='||nm_paciente_w||';NR_ATENDIMENTO_W='||nr_atendimento_w||';NM_MEDICO_W='||nm_medico_w),1,4000);
		else
			ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(1203966,'NR_ATENDIMENTO_W='||nr_atendimento_w||';NM_MEDICO_W='||nm_medico_w),1,4000);
		end if;
		
		exception
		when no_data_found or too_many_rows then
			ds_observacao_w	:= null;
 		end;
	end if;
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
		begin
		select	substr(ds_convenio, 1,40),
			cd_convenio,
			substr(cd_usuario_convenio, 1,30),
			nr_doc_convenio,
			cd_categoria,
			cd_senha,
			nm_medico,
			nr_crm,
			dt_validade_carteira
		into STRICT	ds_convenio_w,
			cd_convenio_w,
			cd_usuario_convenio_w,
			nr_doc_convenio_w,
			cd_categoria_w,
			cd_senha_w,
			nm_medico_w,
			nr_crm_w,
			dt_validade_carteira_w
		from	atendimento_paciente_v
		where	nr_atendimento	= nr_atendimento_w;
		ds_observacao_w := substr(wheb_mensagem_pck.get_texto(301967,'DS_OBSERVACAO_W='||ds_observacao_w||';DS_CONVENIO_W='||ds_convenio_w||';CD_USUARIO_CONVENIO_W='||cd_usuario_convenio_w||';DT_VALIDADE_CARTEIRA_W='||dt_validade_carteira_w||';NR_DOC_CONVENIO_W='||nr_doc_convenio_w||';CD_SENHA_W='||cd_senha_w||';NM_MEDICO_W='||nm_medico_w||';NR_CRM_W='||nr_crm_w),1,4000); /*Fabio 16/08/2004 - Inclui o medico */
		end;
	end if;
	select	nextval('ordem_compra_seq')
	into STRICT	nr_ordem_compra_w
	;
	
	if (cd_setor_atendimento_p > 0) then
		select	obter_nome_setor(cd_setor_atendimento_p)
		into STRICT	ds_setor_w
		;
	end if;
	
	ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(301980,'DS_OBSERVACAO_W='||ds_observacao_w||';DT_ATUALIZACAO_P='||PKG_DATE_FORMATERS.TO_VARCHAR(dt_atualizacao_p, 'timestamp', cd_estabelecimento_p, nm_usuario_p)||';DS_SETOR_W='||ds_setor_w),1,4000);
	
	if (ds_compl_ordem_consig_w IS NOT NULL AND ds_compl_ordem_consig_w::text <> '') then
		begin		
		ds_observacao_w	:= substr(ds_observacao_w || chr(13) || chr(10) || ds_compl_ordem_consig_w,1,4000);
		ds_observacao_w := substr(replace_macro(ds_observacao_w,'@cpf',nr_cpf_w),1,4000);
		ds_observacao_w := substr(replace_macro(ds_observacao_w,'@dt_nascimento',PKG_DATE_FORMATERS.TO_VARCHAR(dt_nascimento_w, 'shortDate', cd_estabelecimento_w,WHEB_USUARIO_PCK.GET_NM_USUARIO)),1,4000);				
		end;
	end if;

	insert into ordem_compra(
		nr_ordem_compra,
		cd_estabelecimento,
		cd_cgc_fornecedor,
		cd_condicao_pagamento,
		cd_comprador,
		dt_ordem_compra,
		dt_atualizacao,
		nm_usuario,
		cd_moeda,
		ie_situacao,
		dt_inclusao,
		cd_pessoa_solicitante,
		cd_cgc_transportador,
		ie_frete,
		vl_frete,
		pr_multa_atraso,
		pr_desc_pgto_antec,
		pr_juros_negociado,
		dt_emissao,
		ds_observacao,
		cd_motivo_alteracao,
		cd_local_entrega,
		dt_entrega,
		nr_prescricao,
		dt_aprovacao,
		dt_baixa,
		ie_aviso_chegada,
		ie_emite_obs,
		ie_urgente,
		nr_atendimento,
		ie_somente_pagto,
		cd_convenio,
		ie_tipo_ordem,
		nr_cirurgia,
		ds_pessoa_contato,
		pr_desconto,
		pr_desc_financeiro,
		cd_centro_custo,
		cd_setor_atendimento)
	values (	nr_ordem_compra_w,
		cd_estabelecimento_w,
		cd_fornecedor_consignado_p,
		cd_condicao_pagamento_padrao_w,
		cd_comprador_padrao_w,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		cd_moeda_padrao_w,
		'A',
		clock_timestamp(),
		coalesce(cd_pessoa_solicitante_p,cd_pessoa_solic_padrao_w),
		null,
		'C',
		0,
		null,
		0,
		0,
		null,
		ds_observacao_w,
		null,
		coalesce(cd_local_entrega_w, cd_local_estoque_w),
		dt_entrega_w,
		nr_prescricao_p,
		null,
		null,
		'N',
		'S',
		'N',
		coalesce(nr_atendimento_w,nr_atendimento_p),
		'N',
		cd_convenio_w,
		'G',
		nr_cirurgia_w,
		nm_pessoa_contato_w,
		tx_desc_ordem_w,
		pr_desc_financeiro_w,
		cd_centro_custo_w,
		cd_setor_atendimento_p);

	begin
	select	coalesce((substr(obter_dados_nf_ref_consumo(nr_seq_nf_entrada,'SEQ',nr_prescricao),1,255))::numeric ,0)
	into STRICT	nr_seq_nf_consumo_w
	from 	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and	cd_material = cd_material_p;
	exception
	when others then
		nr_seq_nf_consumo_w	:= 0;
	end;
	
	if (nr_seq_nf_consumo_w > 0) then
	
		select	count(*)
		into STRICT	qt_nota_fiscal_obs_w
		from	nota_fiscal
		where	nr_sequencia = nr_seq_nf_consumo_w;
		
		select	wheb_mensagem_pck.get_Texto(313112, 'NR_ORDEM_COMPRA_W='|| NR_ORDEM_COMPRA_W) /*Originada da ordem de compra #@NR_ORDEM_COMPRA_W#@*/
		into STRICT	ds_obs_nf_ordem_w
		;	
		
		if (qt_nota_fiscal_obs_w > 0) then
		 
			select	ds_observacao
			into STRICT	ds_obs_nota_consumo_w
			from	nota_fiscal
			where	nr_sequencia = nr_seq_nf_consumo_w;

		else
			ds_obs_nota_consumo_w := '';
		end if;

		ds_obs_nota_consumo_w := substr(ds_obs_nota_consumo_w || ds_obs_nf_ordem_w,1,4000);
		
		update  nota_fiscal
		set	ds_observacao = substr(ds_obs_nota_consumo_w || '.',1,4000)
		where 	nr_sequencia = nr_seq_nf_consumo_w;
	end if;
	end;
end if;

begin
select	coalesce(max(nr_item_oci) + 1,1)
into STRICT	nr_item_oci_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_w;
exception
	when others then
		nr_item_oci_w 	:= 1;
end;


vl_unitario_material_w := obter_valor_mat_ordem_consig(	cd_estabelecimento_p, cd_fornecedor_consignado_p, cd_material_p, nr_ordem_compra_w, nr_prescricao_p, nr_atendimento_p, ie_tipo_atendimento_w, nr_seq_agenda_w, dt_atualizacao_p, nr_seq_lote_fornec_p, cd_convenio_w, cd_categoria_w, clock_timestamp(), vl_unitario_material_w);



ds_observacao_w		:= substr(ds_observacao_p,1,4000);

/* Matheus OS 35418 rotina para buscar a observacao do material e inserir na ordem de compra */

select	max(substr(ds_observacao,1,255)),
	max(substr(coalesce(ds_lote,Obter_dados_lote_fornec(nr_seq_lote_fornec,'D')),1,25)),
	max(to_date(SUBSTR(Obter_dados_lote_fornec(nr_seq_lote_fornec,'V'),1,25),'DD/MM/YYYY')),
	max(nr_seq_marca),
	max(dt_validade),
	max(nr_seq_nf_entrada)
into STRICT	ds_observacao_item_w,
	ds_lote_w,
	dt_validade_lote_w,
	nr_seq_marca_consig_w,
	dt_validade_w,
	nr_seq_nf_entrada_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia	= nr_seq_prescricao_p
and	cd_material 	= cd_material_p;

select	max(nr_cirurgia)
into STRICT	nr_cirurgia_ww
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

if (coalesce(ds_lote_w::text, '') = '') then

	select 	max(SUBSTR(Obter_dados_lote_fornec(nr_seq_lote_fornec_p,'D'),1,25)),
		max(to_date(SUBSTR(Obter_dados_lote_fornec(nr_seq_lote_fornec_p,'V'),1,25),'DD/MM/YYYY'))
	into STRICT	ds_lote_w,
		dt_validade_lote_w
	;
	
end if;	

if (ie_obs_oc_cirurgia_w = 'A') then
	begin
	ds_observacao_item_w	:= substr(wheb_mensagem_pck.get_texto(301983,'NR_CIRURGIA_WW='||nr_cirurgia_ww||';DS_LOTE_W='||ds_lote_w),1,255);
	end;
elsif (ie_obs_oc_cirurgia_w = 'B') then
	begin
	ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(301985,'NR_CIRURGIA_WW='||nr_cirurgia_ww||';DS_LOTE_W='||ds_lote_w||';DS_OBSERVACAO_P='||ds_observacao_p),1,2000);

	if (ds_observacao_item_w IS NOT NULL AND ds_observacao_item_w::text <> '') then
		ds_observacao_item_w	:= substr(ds_observacao_w || chr(13) || chr(10) || ds_observacao_item_w,1,255);
	else
		ds_observacao_item_w	:= substr(ds_observacao_w,1,255);
	end if;
	end;
elsif (ie_obs_oc_cirurgia_w = 'C') then
	begin

	nr_nf_vinculada_w := '';

	if (nr_seq_nf_entrada_w > 0) then
		select	substr(obter_dados_nota_fiscal(nr_seq_nf_entrada_w,'0'),1,255)
		into STRICT	nr_nf_vinculada_w
		;
	end if;

	ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(334010,'NR_CIRURGIA_WW='||nr_cirurgia_ww||';DS_LOTE_W='||ds_lote_w||';DS_OBSERVACAO_P='||ds_observacao_p||';NR_NF_VINC_P='||nr_nf_vinculada_w),1,2000);

	if (ds_observacao_item_w IS NOT NULL AND ds_observacao_item_w::text <> '') then
		ds_observacao_item_w	:= substr(ds_observacao_w || chr(13) || chr(10) || ds_observacao_item_w,1,255);
	else
		ds_observacao_item_w	:= substr(ds_observacao_w,1,255);
	end if;
	end;
elsif (ie_obs_oc_cirurgia_w = 'D') then
	begin
	ds_observacao_item_w	:= substr(wheb_mensagem_pck.get_texto(446373,'NR_CIRURGIA_WW='||nr_cirurgia_ww||';DS_LOTE_W='||ds_lote_w||';DT_VALIDADE_W='||dt_validade_lote_w),1,255);	
	end;
end if;

/*OS 958429 - busca o local de estoque de faturamento direto para o item*/

if (cd_convenio_w > 0) then

	select	coalesce(obter_regra_fatur_direto_mat(cd_material_p, cd_convenio_w, PKG_DATE_UTILS.start_of(clock_timestamp(), 'dd', 0)),'N')
	into STRICT	ie_local_fatur_direto_w
	;
	
	if (ie_local_fatur_direto_w = 'S') then
		cd_local_estoque_w := cd_local_fatur_direto_w;
	end if;	
end if;

ie_tipo_conta_w		:= 3;
if (coalesce(cd_centro_custo_w::text, '') = '') then
	ie_tipo_conta_w	:= 2;
end if;
SELECT * FROM define_conta_material(cd_estabelecimento_w, cd_material_p, ie_tipo_conta_w, null, null, null, null, null, null, null, cd_local_estoque_w, Null, clock_timestamp(), cd_conta_contabil_w, cd_centro_custo_w, null) INTO STRICT cd_conta_contabil_w, cd_centro_custo_w;


nr_item_oci_gerado_p	:= nr_item_oci_w;

ie_atualizou_item_w := 'N';

if (ie_agrupa_oc_novo_item_w = 'S') then
	begin
	select	coalesce(max(nr_item_oci),0)
	into STRICT	nr_item_oci_ww
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_w
	and	cd_material = cd_material_p
	and	nr_seq_lote_fornec = nr_seq_lote_fornec_p;

	if (nr_item_oci_ww > 0) then
		begin
		update	ordem_compra_item
		set	qt_material = (qt_material + qt_compra_w),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp(),
			vl_total_item = round(( (qt_material + qt_compra_w) * vl_unitario_material),4)
		where	nr_ordem_compra = nr_ordem_compra_w
		and	nr_item_oci = nr_item_oci_ww;

		select	max(nr_sequencia)
		into STRICT	nr_seq_entrega_w
		from	ordem_compra_item_entrega
		where	nr_ordem_compra = nr_ordem_compra_w
		and	nr_item_oci = nr_item_oci_ww;

		update	ordem_compra_item_entrega
		set	qt_prevista_entrega = (qt_prevista_entrega + qt_compra_w),
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_ordem_compra = nr_ordem_compra_w
		and	nr_item_oci = nr_item_oci_ww
		and	nr_sequencia = nr_seq_entrega_w;

		ie_atualizou_item_w	:= 'S';
		end;
	end if;
	end;
end if;

if (coalesce(cd_convenio_w,0) = 0) then
	cd_convenio_w := obter_convenio_atendimento(nr_atendimento_p);
end if;

select	obter_regra_oc_opme(cd_estabelecimento_w, cd_fornecedor_consignado_p, cd_convenio_w, cd_material_p)
into STRICT	pr_desc_financ_w
;

if (pr_desc_financ_w < 0) then
	pr_desc_financ_w := 0;
end if;

if (ie_replicar_desc_financ_item_w = 'S') then
	pr_desc_financ_w := pr_desc_financeiro_w;
end if;

if (coalesce(nr_serie_material_p::text, '') = '') then
	select obter_dados_lote_fornec(nr_seq_lote_fornec_p,'NSE')
	into STRICT nr_serie_material_w
	;
else
	nr_serie_material_w := nr_serie_material_p;
end if;

if (ie_atualizou_item_w = 'N') then
	begin
	
	if (coalesce(nr_atendimento_w,0) = 0) then
	
		select	nr_atendimento
		into STRICT	nr_atendimento_w
		from	ordem_compra
		where	nr_ordem_compra = nr_ordem_compra_w;
	
	end if;
	
	insert into ordem_compra_item(
		nr_ordem_compra,
		nr_item_oci,
		cd_material,
		cd_unidade_medida_compra,
		vl_unitario_material,
		qt_material,
		qt_original,
		dt_atualizacao,
		nm_usuario,
		ie_situacao,
		cd_pessoa_solicitante,
		qt_material_entregue,
		pr_descontos,
		cd_local_estoque,
		ds_material_direto,
		ds_observacao,
		cd_motivo_alteracao,
		nr_cot_compra,
		nr_item_cot_compra,
		cd_centro_custo,
		cd_conta_contabil,
		nr_serie_material,
		nr_seq_lote_fornec,
		nr_seq_prescr_item,
		nr_atendimento,
		ds_lote,
		dt_validade,
		nr_seq_marca,
		pr_desc_financ,
		nr_seq_matpaci,
		vl_total_item,
		cd_sequencia_parametro)
	values ( nr_ordem_compra_w,
		nr_item_oci_w,
		cd_material_P,
		cd_unidade_medida_compra_w,
		vl_unitario_material_w,
		qt_compra_w,
		qt_compra_w,
		clock_timestamp(),
		nm_usuario_p,
		'A',
		coalesce(cd_pessoa_solicitante_p,cd_pessoa_solic_padrao_w),
		0,
		coalesce(pr_desconto_p,0),
		coalesce(cd_local_entrega_w, cd_local_estoque_w),
		null,
		ds_observacao_item_w,
		null,
		null,
		null,
		cd_centro_custo_w,
		cd_conta_contabil_w,
		nr_serie_material_w,
		nr_seq_lote_fornec_p,
		nr_seq_prescricao_p,
		nr_atendimento_w,
		ds_lote_w,
		dt_validade_w,
		nr_seq_marca_consig_w,
		pr_desc_financ_w,
		nr_seq_matpaci_p,
		round((qt_compra_w * vl_unitario_material_w)::numeric,4),
		philips_contabil_pck.get_parametro_conta_contabil);

	select  to_date(clock_timestamp() + coalesce(qt_dias_entrega_oc_consig,0))
	into STRICT    dt_prevista_entrega_w
	from	parametro_compras
	where   cd_estabelecimento = cd_estabelecimento_p;

	insert into ordem_compra_item_entrega(
		nr_sequencia,
		nr_ordem_compra,
		nr_item_oci,
		dt_prevista_entrega,
		dt_real_entrega,
		dt_entrega_original,
		dt_entrega_limite,
		qt_prevista_entrega,
		qt_real_entrega,
		dt_atualizacao,
		nm_usuario,
		ds_observacao)
	values (	nextval('ordem_compra_item_entrega_seq'),
		nr_ordem_compra_w,
		nr_item_oci_w,
		dt_prevista_entrega_w,
		null,
		dt_entrega_w,
		dt_entrega_w,
		qt_compra_w,
		null,
		clock_timestamp(),
		nm_usuario_p,
		null);
	
	end;
end if;

select	coalesce(max(ie_avisa_consumo_consig),'N')
into STRICT	ie_avisa_consumo_w
from	parametro_compras
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_avisa_consumo_w = 'S') then
	begin
	CALL avisa_consumo_consig_fornec(
			nr_ordem_compra_w,
			cd_material_p,
			nr_seq_lote_fornec_p,
			qt_compra_w,
			nr_atendimento_p,
			cd_estabelecimento_p);
	exception
	when others then
		ie_erro_email_w	:= 'S';
	end;
end if;

Calcular_Liquido_Ordem_Compra(nr_ordem_compra_w, nm_usuario_p);
if (ie_aprova_auto_w = 'S') then
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	ordem_compra
	where	nr_ordem_compra	= nr_ordem_compra_w
	and	coalesce(dt_aprovacao::text, '') = '';
	/*Para verificar se a OC ja nao esta aprovada ou reprovada*/

	if (qt_existe_w > 0) then
		CALL Gerar_Aprov_Ordem_Compra(nr_ordem_compra_w, null, 'S', nm_usuario_p);
	end if;
	end;
end if;

CALL gerar_ordem_compra_venc(nr_ordem_compra_w, nm_usuario_p);

if (ie_gera_oc_reposicao_p = 'S') then

	CALL gerar_ordem_reposicao_consig(cd_estabelecimento_w,
			cd_fornecedor_consignado_p,
			cd_material_p,
			qt_total_dispensar_p,
			dt_atualizacao_p,
			nm_usuario_p,
			nr_prescricao_p,
			nr_seq_prescricao_p,
			nr_atendimento_p,
			cd_local_estoque_p,
			ds_observacao_p,
			coalesce(cd_local_entrega_p,cd_local_estoque_p),
			cd_centro_custo_w,
			pr_desconto_p,
			nr_serie_material_w,
			cd_pessoa_solicitante_p,
			nr_seq_lote_fornec_p,
			cd_setor_atendimento_p,
			nr_seq_matpaci_p,
			qt_compra_w,
			cd_unidade_medida_compra_w,
			cd_comprador_padrao_w);
end if;

if (nr_ordem_compra_w > 0) then
	nr_ordem_compra_gerada_p	:= nr_ordem_compra_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_ordem_compra_consig ( cd_estabelecimento_p bigint, cd_fornecedor_consignado_p text, cd_material_p bigint, qt_total_dispensar_p bigint, dt_atualizacao_p timestamp, nm_usuario_p text, nr_prescricao_p bigint, nr_seq_prescricao_p bigint, nr_atendimento_p bigint, cd_local_estoque_p bigint, ds_observacao_p text, cd_local_entrega_p bigint, cd_centro_custo_p bigint, pr_desconto_p bigint, nr_serie_material_p text, cd_pessoa_solicitante_p text, nr_seq_lote_fornec_p bigint, cd_setor_atendimento_p bigint, ie_gera_oc_reposicao_p text, nr_seq_matpaci_p bigint, nr_ordem_compra_gerada_p INOUT bigint, nr_item_oci_gerado_p INOUT bigint) FROM PUBLIC;

