CREATE OR REPLACE PROCEDURE gerar_ordem_item_entrega ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_ordem_compra_w	ordem_compra_item.nr_ordem_compra%type;
nr_item_oci_w		ordem_compra_item.nr_item_oci%type;
qt_prevista_entrega_w	ordem_compra_item.qt_material%type;
dt_entrega_w		ordem_compra.dt_entrega%type;


BEGIN

nr_ordem_compra_w := nr_ordem_compra_p;
nr_item_oci_w := nr_item_oci_p;

select 	qt_material
into STRICT 	qt_prevista_entrega_w
from 	ordem_compra_item
where 	nr_ordem_compra = nr_ordem_compra_w
and 	nr_item_oci = nr_item_oci_w;

select 	dt_entrega
into STRICT 	dt_entrega_w
from 	ordem_compra
where 	nr_ordem_compra = nr_ordem_compra_w;

insert into ordem_compra_item_entrega(
	nr_sequencia,
	nr_ordem_compra,
	nr_item_oci,
	dt_prevista_entrega,
	dt_real_entrega,
	dt_entrega_original,
	dt_entrega_limite,
	qt_prevista_entrega,
	qt_real_entrega,
	dt_atualizacao,
	nm_usuario,
	ds_observacao)
values (	nextval('ordem_compra_item_entrega_seq'),
	nr_ordem_compra_w,
	nr_item_oci_w,
	dt_entrega_w,
	null,
	dt_entrega_w,
	dt_entrega_w,
	qt_prevista_entrega_w,
	null,
	clock_timestamp(),
	nm_usuario_p,
	null);

commit;

END;
