SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_orientacao_preparo_onc (nr_prescricao_p prescr_medica.nr_prescricao%type) AS $body$
DECLARE


ds_orientacao_w varchar(2000);

c01 CURSOR FOR
SELECT  p.nr_sequencia,
        p.nr_seq_material
from    prescr_material p
where   p.nr_prescricao = nr_prescricao_p
and     p.ie_agrupador = 1
and     coalesce(p.ds_diluicao_edit::text, '') = ''
order by p.nr_sequencia;

BEGIN
    for material in c01 loop
        ds_orientacao_w := obter_orientacao_preparo_onc(nr_prescricao_p, material.nr_seq_material);

        update prescr_material
        set ds_diluicao_edit = ds_orientacao_w
        where nr_prescricao = nr_prescricao_p
        and nr_sequencia = material.nr_sequencia;
    end loop;

    commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
