from	proj_equipe_papel pp,
	proj_equipe pe
where	pp.nr_seq_equipe = pe.nr_sequencia
and	pp.nr_seq_funcao = 44
and	pp.ie_funcao_rec_migr = 'C'
and	pe.nr_seq_equipe_funcao = 11
and	coalesce(pp.ie_situacao,'A') = 'A'
