		values (
			nr_seq_os_nec_vinc_w,
			clock_timestamp(),
			cd_pessoa_usuario_w,
			1272,
			41,
			' ',
			'A',
			' ',
			cd_funcao_w,
			nr_seq_grupo_des_migr_w,
			'S',
			4,
			'2',
			clock_timestamp(),
			clock_timestamp() + interval '7 days',
			clock_timestamp(),
			clock_timestamp() + interval '7 days',
			732,
			2,
			1,
			nm_usuario_p,
			clock_timestamp(),
			'N',
			'N',
			nr_seq_os_teste_p,
			'P',
			nm_usuario_p,
			clock_timestamp(),
			'1',
			null,
			22,
			1,
			6);

		/*delete
		from	man_ordem_servico_exec
