		open c01;
		loop
		fetch c01 into nm_usuario_programador_w;
		exit when c01%notfound;
			begin
			if	(nm_usuario_programador_w is not null) then
				begin
				insert into man_ordem_servico_exec (
					nr_sequencia,
					nr_seq_ordem,
					dt_atualizacao,
					nm_usuario,
					nm_usuario_exec,
					qt_min_prev,
					dt_ult_visao,
					nr_seq_funcao,
					dt_recebimento,
