			end;
		end loop;
		close c01;*/
		insert into proj_ordem_servico(
			nr_sequencia,
			nr_seq_proj,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_ordem,
			ie_tipo_ordem)
		values (
			nextval('proj_ordem_servico_seq'),
			nr_seq_projeto_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_os_nec_vinc_w,
			'C');

		update	w_plan_teste_migr_proj
		set	ie_status = 'C'
		where	nr_seq_projeto = nr_seq_projeto_w;
		end;
	end if;
	end;
end if;
nr_seq_os_correcao_p := nr_seq_os_nec_vinc_w;
commit;
end;
