CREATE OR REPLACE PROCEDURE gerar_os_nec_vinc_proj_migr ( nr_seq_os_projeto_p bigint, nm_usuario_p text, nr_seq_os_nec_vinc_p INOUT bigint) AS $body$
DECLARE


cd_pessoa_usuario_w	varchar(10);
cd_funcao_projeto_w	integer;
nr_seq_grupo_des_proj_w	bigint;
nr_seq_projeto_w	bigint;
nr_seq_os_nec_vinc_w	bigint;
nr_seq_vinc_proj_w	bigint;
ds_dano_padrao_w	varchar(4000);


BEGIN
if (nr_seq_os_projeto_p IS NOT NULL AND nr_seq_os_projeto_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	max(cd_funcao),
		max(nr_seq_grupo_des)
	into STRICT	cd_funcao_projeto_w,
		nr_seq_grupo_des_proj_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_os_projeto_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_projeto_w
	from	proj_projeto
	where	nr_seq_ordem_serv = nr_seq_os_projeto_p
	and	cd_funcao = cd_funcao_projeto_w;

	if (nr_seq_projeto_w IS NOT NULL AND nr_seq_projeto_w::text <> '') then
		begin
		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_usuario_w
		from	usuario
		where	nm_usuario = nm_usuario_p;

		select	nextval('man_ordem_servico_seq')
		into STRICT	nr_seq_os_nec_vinc_w
		;

		ds_dano_padrao_w :=
			'# Dados do Projeto:' || chr(13) || chr(10) ||
			'- Projeto: informar o nome do projeto correspondente, exemplo AtePac_EH.' || chr(13) || chr(10) ||
			'- Package: informar o caminho completo da package da classe correspondente, exemplo br.com.AtePac_EH.evolucoes.' || chr(13) || chr(10) ||
			'- Classe: informar o nome da classe correspondente, exemplo EvolucaoWJP.' || chr(13) || chr(10) ||
			'- Objeto / Componente na Classe: informar o nome do objeto / componente correspondente na classe, exemplo resumoEvolucaoWCP.' || chr(13) || chr(10) ||
			'- Evento: informar o nome do evento correspondente ao objeto, exemplo onNewRecord.' || chr(13) || chr(10) || chr(13) || chr(10) ||
			'# Dados do Relatório:' || chr(13) || chr(10) ||
			'- Relatório (Classificação e Código): informar a classificação e o código do relatório, exemplo WATE5412.' || chr(13) || chr(10) ||
			'- Output NetBeans: registrar, através de anexo, as informações geradas no output do NetBeans referente a execução do relatório.' || chr(13) || chr(10) || chr(13) || chr(10) ||
			'# Dados do Dicionário de Dados:' || chr(13) || chr(10) ||
			'- Tabela: informar o nome da tabela, exemplo EVOLUCAO_PACIENTE.' || chr(13) || chr(10) ||
			'- Visão: informar o número da visão da tabela, exemplo 1234.' || chr(13) || chr(10) ||
			'- Atributo: informar o nome do atributo da tabela, exemplo NR_SEQUENCIA.' || chr(13) || chr(10) ||
			'- Objeto PL/SQL: informar o nome do objeto PL/SQL, exemplo Obter_Nome_Pf.' || chr(13) || chr(10) || chr(13) || chr(10) ||
			'# Dados do Dicionário de Objetos:' || chr(13) || chr(10) ||
			'- Objeto: informar o código do objeto, exemplo 12345.' || chr(13) || chr(10) || chr(13) || chr(10) ||
			'# Dados do Acesso ao Tasy:' || chr(13) || chr(10) ||
			'- Função: informar o nome da função, exemplo PEP.' || chr(13) || chr(10) ||
			'- Perfil: informar o perfil de acesso a função, exemplo Pac-Médico.' || chr(13) || chr(10) ||
			'- Forma de Acesso Externo: informar a forma de acesso a função nos casos de chamadas externas.' || chr(13) || chr(10) ||
			'- Parametrização: informar dados relevantes referentes a parametrização em relação ao acesso a função para reprodução da situação.' || chr(13) || chr(10) || chr(13) || chr(10) ||
			'# Dados da OS:' || chr(13) || chr(10) ||
			'- Anexos: registrar anexos relevantes.' || chr(13) || chr(10) ||
			'- Documentação: descrever o problema / situação identificada.';

		insert into man_ordem_servico(
			nr_sequencia,
			dt_ordem_servico,
			cd_pessoa_solicitante,
			nr_seq_localizacao,
			nr_seq_equipamento,
			ds_dano_breve,
			ie_prioridade,
			ds_dano,
			cd_funcao,
			nr_seq_grupo_des,
			ie_classificacao,
			ie_tipo_ordem,
			ie_status_ordem,
			dt_inicio_previsto,
			dt_fim_previsto,
			dt_inicio_desejado,
			dt_conclusao_desejada,
			nr_seq_estagio,
			nr_grupo_trabalho,
			nr_grupo_planej,
			nm_usuario,
			dt_atualizacao,
			ie_parado,
			ie_obriga_news,
			nr_seq_origem,
			ie_exclusiva,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			ie_origem_os,
			nr_seq_complex,
			nr_seq_classif,
			ie_teste_performance,
			nr_seq_nivel_valor)
		values (
			nr_seq_os_nec_vinc_w,
			clock_timestamp(),
			cd_pessoa_usuario_w,
			1272,
			41,
			' ',
			'A',
			ds_dano_padrao_w,
			cd_funcao_projeto_w,
			null,--nr_seq_grupo_des_proj_w,
			'S',
			4,
			'2',
			clock_timestamp(),
			clock_timestamp() + interval '7 days',
			clock_timestamp(),
			clock_timestamp() + interval '7 days',
			1051,
			2,
			1,
			nm_usuario_p,
			clock_timestamp(),
			'N',
			'N',
			nr_seq_os_projeto_p,
			'P',
			nm_usuario_p,
			clock_timestamp(),
			'1',
			3,
			58,
			'N',
			1);

		delete
		from	man_ordem_servico_exec
		where	nr_seq_ordem = nr_seq_os_nec_vinc_w;

		delete
		from	man_ordem_ativ_prev
		where	nr_seq_ordem_serv = nr_seq_os_nec_vinc_w;

		insert into man_ordem_servico_exec(
			nr_sequencia,
			nr_seq_ordem,
			dt_atualizacao,
			nm_usuario,
			nm_usuario_exec,
			qt_min_prev,
			dt_ult_visao,
			nr_seq_funcao,
			dt_recebimento,
			nr_seq_tipo_exec)
		values (
			nextval('man_ordem_servico_exec_seq'),
			nr_seq_os_nec_vinc_w,
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			null,
			null,
			null,
			null,
			5);

		insert into man_ordem_servico_exec(
			nr_sequencia,
			nr_seq_ordem,
			dt_atualizacao,
			nm_usuario,
			nm_usuario_exec,
			qt_min_prev,
			dt_ult_visao,
			nr_seq_funcao,
			dt_recebimento,
			nr_seq_tipo_exec)
		values (
			nextval('man_ordem_servico_exec_seq'),
			nr_seq_os_nec_vinc_w,
			clock_timestamp(),
			nm_usuario_p,
			'Jerusa',
			null,
			null,
			null,
			null,
			1);

		select	nextval('proj_ordem_servico_seq')
		into STRICT	nr_seq_vinc_proj_w
		;

		insert into proj_ordem_servico(
			nr_sequencia,
			nr_seq_proj,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_ordem,
			ie_tipo_ordem)
		values (
			nr_seq_vinc_proj_w,
			nr_seq_projeto_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_os_nec_vinc_w,
			'V');
		end;
	end if;
	end;
end if;
nr_seq_os_nec_vinc_p := nr_seq_os_nec_vinc_w;
commit;
end;
