SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_os_revisao_expressao ( cd_expressao_p bigint, ds_expressao_p text, DS_REVISAO_p text, nm_usuario_p text) AS $body$
DECLARE

cd_pessoa_fisica_w	varchar(10);
nr_seq_ordem_w		bigint;
ds_dano_breve_w		varchar(80);
ds_dano_w		varchar(4000);



BEGIN

	ds_dano_breve_w	:= substr('Review expression: '||trim(both cd_expressao_p|| ' '||ds_expressao_p),1,80);
	ds_dano_w	:= 'Review expression: ';

	if (cd_expressao_p IS NOT NULL AND cd_expressao_p::text <> '') then
		ds_dano_w	:= ds_dano_w ||chr(10)||chr(13) ||'Code :'||cd_expressao_p;
	end if;

	if (ds_expressao_p IS NOT NULL AND ds_expressao_p::text <> '') then
		ds_dano_w	:= ds_dano_w ||chr(10)||chr(13) ||'Expression :'||ds_expressao_p;
	end if;

	if (DS_REVISAO_p IS NOT NULL AND DS_REVISAO_p::text <> '') then
		ds_dano_w	:= ds_dano_w ||chr(10)||chr(13) ||'Review :'|| DS_REVISAO_p;
	end if;


	select	nextval('man_ordem_servico_seq')
	into STRICT	nr_seq_ordem_w
	;

	select	obter_pessoa_fisica_usuario(nm_usuario_p, 'C')
	into STRICT	cd_pessoa_fisica_w
	;

	insert into man_ordem_servico(
		nr_sequencia,
		cd_pessoa_solicitante,
		dt_ordem_servico,
		ie_prioridade,
		ie_parado,
		ds_dano_breve,
		dt_atualizacao,
		nm_usuario,
		ds_dano,
		ie_tipo_ordem,
		ie_plataforma,
		ie_classificacao,
		nr_seq_estagio,
		nr_seq_complex,
		nr_seq_equipamento,
		nr_seq_localizacao,
		nr_grupo_trabalho,
		nr_grupo_planej,
		ie_resp_teste,
		cd_funcao,
		nr_seq_grupo_des,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_status_ordem,
		ie_origem_os)
	values (	nr_seq_ordem_w,
			cd_pessoa_fisica_w,
			clock_timestamp(),
			'M',
			'N',
			ds_dano_breve_w,
			clock_timestamp(),
			nm_usuario_p,
			ds_dano_w,
			1,
			'H',
			'S',
			null,
			2,
			null,
			null,
			null,
			null,
			'A',
			363,
			null, --Grupo express√µes
			clock_timestamp(),
			nm_usuario_p,
			1,
			1);




commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
