SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_outros_valore_ret_movto (nr_seq_retorno_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_convenio_w		bigint;
cd_estabelecimento_w	bigint;
cd_item_w		bigint;
nr_seq_ret_item_w	bigint;
vl_pago_w		double precision;
count_w			bigint;
nr_seq_trans_finac_w	bigint;

c01 CURSOR FOR
SELECT	somente_numero(a.cd_item),
	a.nr_seq_ret_item,
	a.vl_total_pago
from	convenio_retorno_movto a,
	convenio_retorno_item b
where	b.nr_sequencia		= a.nr_seq_ret_item
and	b.nr_seq_retorno	= nr_seq_retorno_p
and	coalesce(a.cd_item,0)	<> 0
and	a.vl_total_pago 	> 0;


BEGIN

select	max(cd_convenio),
	max(cd_estabelecimento)
into STRICT	cd_convenio_w,
	cd_estabelecimento_w
from	convenio_retorno
where	nr_sequencia	= nr_seq_retorno_p;

select	count(*)
into STRICT	count_w
from	conv_regra_retorno_adic
where	cd_convenio		= cd_convenio_w
and	cd_estabelecimento	= cd_estabelecimento_w;

if (count_w > 0) then

	open C01;
	loop
	fetch C01 into
		cd_item_w,
		nr_seq_ret_item_w,
		vl_pago_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	max(nr_seq_trans_financ)
		into STRICT	nr_seq_trans_finac_w
		from	conv_regra_retorno_adic
		where	cd_convenio		= cd_convenio_w
		and	cd_estabelecimento	= cd_estabelecimento_w
		and	cd_procedimento		= cd_item_w;

		if (nr_seq_trans_finac_w IS NOT NULL AND nr_seq_trans_finac_w::text <> '') then

			insert	into convenio_ret_item_valor(dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_ret_item,
				nr_seq_trans_financ,
				nr_sequencia,
				vl_adicional)
			values (clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_p,
				nr_seq_ret_item_w,
				nr_seq_trans_finac_w,
				nextval('convenio_ret_item_valor_seq'),
				vl_pago_w);

			update	convenio_retorno_item
			set	vl_adicional	= vl_adicional - vl_pago_w
			where	nr_sequencia	= nr_seq_ret_item_w
			and	vl_adicional 	> 0;

			--Zerar o valor adicional pois nao pode ser negativo
			update	convenio_retorno_item
			set	vl_adicional	= 0
			where	nr_sequencia	= nr_seq_ret_item_w
			and	vl_adicional	< 0;

		end if;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
