CREATE OR REPLACE PROCEDURE gerar_padrao_medic_medico ( cd_pessoa_fisica_p text, dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE

cd_material_w	integer;

C01 CURSOR FOR
	SELECT 	distinct(b.cd_material)
	from 		prescr_material b,
			Prescr_medica a
	where 	a.nr_prescricao	= b.nr_prescricao
	  and 	a.cd_medico		= cd_pessoa_fisica_p
	  and		a.dt_prescricao	between dt_inicial_p
						    and dt_final_p
	  and 	not exists (SELECT cd_material
			from medico_material x
			where cd_pessoa_fisica = cd_pessoa_fisica_p);


BEGIN

OPEN C01;
LOOP
	FETCH C01 into cd_material_w;
      EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert into medico_material(
			cd_pessoa_fisica,
			cd_material,
			dt_atualizacao,
			nm_usuario)
		values (
			cd_pessoa_fisica_p,
			cd_material_w,
			clock_timestamp(),
			nm_usuario_p);
		end;
END LOOP;
CLOSE C01;
COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
